import bcrypt from 'bcryptjs'
import { supabase } from '../../../../lib/supabaseClient'
import { supabaseAdmin } from '../../../../lib/supabaseAdmin'
import { verifyToken, generateTokenPair } from '../../../../lib/jwt'

/**
 * Normalise un pr√©nom/nom pour cr√©er un email valide
 * Supprime accents, c√©dilles et caract√®res sp√©ciaux
 * Jos√© ‚Üí jose, Mart√≠nez ‚Üí martinez, B√©nard ‚Üí benard
 * IDENTIQUE √Ä login.js pour coh√©rence
 */
const normalizeForEmail = (text) => {
    return text
        .toLowerCase()
        .trim()
        .normalize('NFD')                    // D√©compose les caract√®res accentu√©s
        .replace(/[\u0300-\u036f]/g, '')    // Supprime les marques diacritiques
        .replace(/[^a-z0-9]/g, '')          // Garde seulement lettres et chiffres
}

export default async function handler(req, res) {
    if (req.method !== 'POST') {
        return res.status(405).json({ error: 'Method not allowed' })
    }

    const { currentPassword, newPassword } = req.body

    // Validation des entr√©es
    if (!currentPassword || !newPassword) {
        return res.status(400).json({ 
            error: 'Mot de passe actuel et nouveau mot de passe requis' 
        })
    }

    if (newPassword.trim().length < 6) {
        return res.status(400).json({ 
            error: 'Le nouveau mot de passe doit contenir au moins 6 caract√®res' 
        })
    }

    // V√©rifier le token JWT
    const authHeader = req.headers.authorization
    if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ error: 'Token manquant ou invalide' })
    }

    const token = authHeader.split(' ')[1]
    const decodedToken = verifyToken(token)
    
    if (!decodedToken) {
        return res.status(401).json({ error: 'Token invalide ou expir√©' })
    }

    try {
        // 1. R√©cup√©rer l'utilisateur depuis la table users
        console.log(`[DEBUG] R√©cup√©ration user pour token ID: ${decodedToken.id}`)
        const { data: user, error: userError } = await supabase
            .from('users')
            .select('*')
            .eq('id', decodedToken.id)
            .single()

        if (userError || !user) {
            console.error(`[DEBUG] User non trouv√© - error:`, userError)
            return res.status(404).json({ error: 'Utilisateur non trouv√©' })
        }

        console.log(`[DEBUG] User r√©cup√©r√©:`, {
            id: user.id,
            prenom: user.prenom,
            nom: user.nom,
            email: user.email,
            password_hash: user.password_hash ? 'EXISTE' : 'NULL',
            custom_password: user.custom_password || 'NULL'
        })

        // 2. V√©rifier le mot de passe actuel (LOGIQUE IDENTIQUE √Ä login.js)
        let currentPasswordValid = false

        console.log(`[DEBUG] V√©rification mot de passe actuel pour ${user.prenom} ${user.nom}`)
        console.log(`[DEBUG] password_hash exists: ${user.password_hash ? 'EXISTE' : 'NULL'}`)

        if (user.password_hash) {
            console.log(`[DEBUG] Utilisation bcrypt pour v√©rification`)
            // Si password_hash existe, utiliser uniquement bcrypt
            currentPasswordValid = await bcrypt.compare(currentPassword, user.password_hash)
            if (!currentPasswordValid) {
                return res.status(400).json({ error: 'Mot de passe actuel incorrect' })
            }
        } else {
            console.log(`[DEBUG] Utilisation fallback nom normalis√©`)
            // Fallback : premi√®re connexion avec nom normalis√© (B√©nard ‚Üí benard)
            const nomNormalized = normalizeForEmail(user.nom)
            const currentPasswordNormalized = normalizeForEmail(currentPassword)
            
            console.log(`[DEBUG] Comparaison normalis√©e:`)
            console.log(`[DEBUG]   nom "${user.nom}" ‚Üí "${nomNormalized}"`)
            console.log(`[DEBUG]   currentPassword "${currentPassword}" ‚Üí "${currentPasswordNormalized}"`)
            console.log(`[DEBUG]   match: ${currentPasswordNormalized === nomNormalized}`)
            
            if (currentPasswordNormalized !== nomNormalized) {
                return res.status(400).json({ error: 'Mot de passe actuel incorrect' })
            }
            currentPasswordValid = true
        }

        // 3. Hasher le nouveau mot de passe
        const saltRounds = 10
        console.log(`[DEBUG] G√©n√©ration hash pour user ID ${user.id}, saltRounds: ${saltRounds}`)
        const newPasswordHash = await bcrypt.hash(newPassword, saltRounds)
        console.log(`[DEBUG] Hash g√©n√©r√© (longueur: ${newPasswordHash.length}): ${newPasswordHash.substring(0, 20)}...`)

        // 4. Mettre √† jour le mot de passe dans la table users avec client ADMIN
        console.log(`[DEBUG] Tentative UPDATE ADMIN pour user ID ${user.id}`)
        console.log(`[DEBUG] Donn√©es √† mettre √† jour:`, {
            password_hash: newPasswordHash.substring(0, 20) + '...',
            custom_password: null,
            must_change_password: false,
            password_changed_at: new Date().toISOString()
        })

        const { data: updateData, error: updateError, count } = await supabaseAdmin
            .from('users')
            .update({
                password_hash: newPasswordHash,
                custom_password: null,  // Supprimer l'ancien syst√®me
                must_change_password: false,
                password_changed_at: new Date().toISOString()
            })
            .eq('id', user.id)
            .select() // CRITIQUE: Forcer le retour des donn√©es mises √† jour

        console.log(`[DEBUG] R√©sultat UPDATE - error:`, updateError)
        console.log(`[DEBUG] R√©sultat UPDATE - data:`, updateData)
        console.log(`[DEBUG] R√©sultat UPDATE - count:`, count)

        if (updateError) {
            console.error('Erreur mise √† jour mot de passe:', updateError)
            return res.status(500).json({ error: 'Erreur lors de la mise √† jour' })
        }

        // 4.5. V√©rification post-update - relire l'utilisateur pour confirmer
        console.log(`[DEBUG] V√©rification post-update pour user ID ${user.id}`)
        const { data: verifyUser, error: verifyError } = await supabaseAdmin
            .from('users')
            .select('id, prenom, nom, password_hash, password_changed_at')
            .eq('id', user.id)
            .single()

        if (verifyError) {
            console.error('Erreur v√©rification post-update:', verifyError)
        } else {
            console.log(`[DEBUG] User apr√®s UPDATE:`, {
                id: verifyUser.id,
                prenom: verifyUser.prenom,
                nom: verifyUser.nom,
                password_hash: verifyUser.password_hash ? verifyUser.password_hash.substring(0, 20) + '...' : 'NULL',
                password_changed_at: verifyUser.password_changed_at
            })
        }

        // 5. G√©n√©rer de nouveaux tokens avec mustChangePassword = false
        const userData = {
            id: user.id,
            formateur_id: user.id,
            username: user.prenom,
            nom: user.nom,
            prenom: user.prenom,
            email: user.email,
            mustChangePassword: false
        }

        const newTokens = generateTokenPair(userData)

        // 6. Log de s√©curit√©
        console.log(`[AUTH] Mot de passe chang√© pour formateur ${user.prenom} ${user.nom} (ID: ${user.id})`)

        // 7. Retourner la r√©ponse
        res.status(200).json({
            success: true,
            message: 'Mot de passe chang√© avec succ√®s',
            user: {
                id: user.id,
                username: user.prenom,
                nom: user.nom,
                prenom: user.prenom,
                email: user.email,
                role: 'formateur',
                mustChangePassword: false
            },
            tokens: {
                accessToken: newTokens.accessToken,
                refreshToken: newTokens.refreshToken,
                expiresIn: newTokens.expiresIn,
                tokenType: newTokens.tokenType
            }
        })

    } catch (error) {
        console.error('Erreur changement mot de passe:', error)
        res.status(500).json({ 
            error: 'Erreur interne du serveur' 
        })
    }
}

