console.log(`   ‚îî‚îÄ ${fichiersVercel.length} fichiers trouv√©s sur Vercel`);

// Cr√©er la liste compl√®te des fichiers √† auditer
const fichiersUniques = new Set();
fichiersLocaux.forEach(f => fichiersUniques.add(f.cheminRelatif));
fichiersVercel.forEach(f => fichiersUniques.add(f.cheminRelatif));

const fichiersList = Array.from(fichiersUniques).sort();

console.log(`\nüìã AUDIT EXHAUSTIF: ${fichiersList.length} fichiers uniques √† comparer`);
console.log('='.repeat(80));

// Compteurs d√©taill√©s
let compteurs = {
    identiques: 0,
    quasiIdentiques: 0,
    similaires: 0,
    differents: 0,
    tresDifferents: 0,
    localSeulement: 0,
    vercelSeulement: 0,
