      {/* Boutons +/- identiques √† l'existant */}
      <div className="no-print" style={{ display: 'flex', gap: '3px', justifyContent: 'center', marginTop: '3px' }}>
        <button
          onClick={() => onAddApprenant(dayIdx, lieuIdx, creneauName)}
          style={{
            padding: '3px 6px',
            background: '#3b82f6',
            color: 'white',
            border: 'none',
            borderRadius: '3px',
            fontSize: '10px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}
          disabled={disabled}
        >
          +
        </button>
        {selectedApprenants.length > 1 && (
          <button
            onClick={() => onRemoveApprenant(dayIdx, lieuIdx, creneauName)}
            style={{
              padding: '3px 6px',
              background: '#ef4444',
              color: 'white',
              border: 'none',
              borderRadius: '3px',
              fontSize: '10px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
            disabled={disabled}
          >
            ‚àí
          </button>
        )}
      </div>

      </div>
    </>
  );
}

