                    color: 'white', 
                    label: 'ABS',
                    nom: 'Absent'
                }
            case 'dispo':
                return { 
                    backgroundColor: '#f59e0b', 
                    color: 'white', 
                    label: 'DISPO',
                    nom: 'Dispo exceptionnelle'
                }
            case 'planning_type':
                return { 
                    backgroundColor: '#22c55e', 
                    color: 'white', 
                    label: 'DISPO',
                    nom: 'Planning habituel'
                }
            case 'dispo_except':
                return { 
                    backgroundColor: '#f59e0b', 
                    color: 'white', 
                    label: 'EXCEPT',
                    nom: 'Dispo exceptionnelle'
                }
            case 'libre':
            default:
                return { 
                    backgroundColor: '#d1d5db', 
                    color: '#374151', 
                    label: '',
                    nom: 'Libre'
                }
        }
    }

