                    }}>
                        <table style={{ 
                            width: '100%', 
                            borderCollapse: 'separate',
                            borderSpacing: '6px'
                        }}>
                            <thead>
                                <tr>
                                    <th style={{ 
                                        padding: '8px 4px',
                                        fontSize: '12px',
                                        fontWeight: '600',
                                        color: '#6b7280',
                                        textAlign: 'center',
                                        width: '80px'
                                    }}>
                                        Cr√©neaux
                                    </th>
                                    {jours.map(jour => (
                                        <th key={jour} style={{ 
                                            padding: '8px 4px',
                                            fontSize: '11px',
                                            fontWeight: '600',
                                            color: '#6b7280',
                                            textAlign: 'center'
                                        }}>
                                            {jour.substring(0, 3)}
                                        </th>
                                    ))}
                                </tr>
                            </thead>
                            <tbody>
                                {creneaux.map(creneau => (
                                    <tr key={creneau}>
                                        <td style={{ 
                                            padding: '8px 4px',
                                            fontSize: '11px',
                                            fontWeight: '600',
                                            color: '#374151',
                                            textAlign: 'center',
                                            backgroundColor: creneau === 'Matin' ? '#fef3c7' : '#dbeafe',
                                            borderRadius: '6px'
                                        }}>
                                            {creneau === 'Matin' ? 'M' : 'AM'}
                                        </td>
                                        {jours.map(jour => {
                                            const creneauInfo = getCreneauInfo(jour, creneau)
                                            
                                            return (
                                                <td key={`${jour}-${creneau}`} style={{ 
                                                    padding: '0',
                                                    textAlign: 'center'
                                                }}>
                                                    <div style={{
                                                        minHeight: '45px',
                                                        backgroundColor: creneauInfo ? '#3b82f6' : '#f3f4f6',
                                                        color: creneauInfo ? 'white' : '#d1d5db',
                                                        borderRadius: '6px',
                                                        display: 'flex',
                                                        alignItems: 'center',
                                                        justifyContent: 'center',
                                                        padding: '4px',
                                                        border: creneauInfo ? 
                                                            `3px solid ${getLieuCouleur(creneauInfo.lieu_id)}` : 
                                                            '1px solid #e5e7eb'
                                                    }}>
                                                        {creneauInfo ? (
                                                            <div style={{ 
                                                                fontSize: '12px',
                                                                fontWeight: 'bold',
                                                                textAlign: 'center'
                                                            }}>
                                                                {getLieuInitiales(creneauInfo.lieu_id)}
                                                            </div>
                                                        ) : (
                                                            <div style={{ 
                                                                fontSize: '14px',
                                                                color: '#d1d5db'
                                                            }}>
                                                                -
                                                            </div>
                                                        )}
                                                    </div>
                                                </td>
                                            )
                                        })}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>

                    {/* Colonne droite - D√©tails des cr√©neaux */}
                    <div style={{
                        backgroundColor: '#f8fafc',
                        padding: '16px',
                        borderRadius: '12px'
                    }}>
                        <h4 style={{ 
                            fontSize: '14px', 
                            fontWeight: '600', 
                            color: '#374151',
                            margin: '0 0 12px 0'
                        }}>
                            üìÖ D√©tail des disponibilit√©s valid√©es
                        </h4>
                        <div style={{ 
                            display: 'flex', 
                            flexDirection: 'column', 
                            gap: '6px',
                            maxHeight: '400px',
                            overflowY: 'auto'
                        }}>
                            {planningType.map((creneau, index) => (
                                <div key={index} style={{
                                    display: 'flex',
                                    justifyContent: 'space-between',
                                    alignItems: 'center',
                                    padding: '10px 12px',
                                    backgroundColor: '#eff6ff',
                                    borderRadius: '6px',
                                    border: `2px solid ${getLieuCouleur(creneau.lieu_id)}`,
                                    fontSize: '13px'
                                }}>
                                    <div style={{ 
                                        fontWeight: '600', 
                                        color: '#1e40af'
                                    }}>
                                        {creneau.jour} {creneau.creneau === 'Matin' ? 'M' : 'AM'}
                                    </div>
                                    <div style={{ 
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '6px'
                                    }}>
                                        <div style={{
                                            width: '12px',
                                            height: '12px',
                                            backgroundColor: getLieuCouleur(creneau.lieu_id),
                                            borderRadius: '2px'
                                        }} />
                                        <span style={{ 
                                            color: '#6b7280',
                                            fontSize: '12px'
                                        }}>
                                            {getLieuNom(creneau.lieu_id)}
                                        </span>
                                    </div>
                                </div>
                            ))}
                        </div>

                        {/* Stats validation compactes */}
                        {statsValidation && (
                            <div style={{
                                marginTop: '16px',
                                backgroundColor: '#ecfdf5',
                                border: '2px solid #10b981',
                                padding: '12px',
                                borderRadius: '8px'
                            }}>
                                <div style={{ color: '#065f46', fontSize: '12px' }}>
                                    <div style={{ fontWeight: 'bold', marginBottom: '4px' }}>
                                        üìä {statsValidation.totalCreneaux} cr√©neaux valid√©s
                                    </div>
                                    {statsValidation.dateValidation && (
                                        <div>Valid√© le {new Date(statsValidation.dateValidation).toLocaleDateString('fr-FR')}</div>
                                    )}
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            )}
        </div>
    )
}

