import jwt from 'jsonwebtoken'

// Cl√©s secr√®tes pour JWT (√† mettre dans les variables d'environnement)
const JWT_SECRET = process.env.JWT_SECRET || 'aclef-formateur-secret-2024-change-me-in-production'
const REFRESH_SECRET = process.env.JWT_REFRESH_SECRET || 'aclef-refresh-secret-2024-change-me-in-production'

// Dur√©es de vie des tokens
const ACCESS_TOKEN_EXPIRE = '15m' // Token d'acc√®s : 15 minutes
const REFRESH_TOKEN_EXPIRE = '7d' // Token de rafra√Æchissement : 7 jours

/**
 * G√©n√®re un token d'acc√®s JWT
 */
