import { NextResponse } from 'next/server'

// Map pour stocker les tentatives par IP
const requestCounts = new Map()

function rateLimitExceeded(ip) {
  const now = Date.now()
  const windowStart = now - 3600000 // 1 heure (plus g√©n√©reux)
  
  if (!requestCounts.has(ip)) {
    requestCounts.set(ip, [])
  }
  
  const requests = requestCounts.get(ip)
  
  // Nettoyer les anciennes requ√™tes
  const recentRequests = requests.filter(time => time > windowStart)
  requestCounts.set(ip, recentRequests)
  
  // Ajouter la requ√™te actuelle
  recentRequests.push(now)
  
  // Limiter √† 1000 requ√™tes par heure (tr√®s g√©n√©reux pour usage normal)
  return recentRequests.length > 1000
}

export function middleware(request) {
  const ip = request.ip || request.headers.get('x-forwarded-for') || 'unknown'
  
  if (rateLimitExceeded(ip)) {
    return new Response('Too Many Requests - Limite de 1000 requ√™tes par heure d√©pass√©e', { 
      status: 429,
      headers: {
        'Retry-After': '3600'
      }
    })
  }
  
  return NextResponse.next()
}

export const config = {
  matcher: '/api/:path*'
}

