            // Fallback : premi√®re connexion avec nom normalis√© (B√©nard ‚Üí benard)
            const nomNormalized = normalizeForEmail(user.nom)
            const currentPasswordNormalized = normalizeForEmail(currentPassword)
            
            console.log(`[DEBUG] Comparaison normalis√©e:`)
            console.log(`[DEBUG]   nom "${user.nom}" ‚Üí "${nomNormalized}"`)
            console.log(`[DEBUG]   currentPassword "${currentPassword}" ‚Üí "${currentPasswordNormalized}"`)
            console.log(`[DEBUG]   match: ${currentPasswordNormalized === nomNormalized}`)
            
            if (currentPasswordNormalized !== nomNormalized) {
                return res.status(400).json({ error: 'Mot de passe actuel incorrect' })
            }
            currentPasswordValid = true
        }

        // 3. Hasher le nouveau mot de passe
        const saltRounds = 10
        console.log(`[DEBUG] G√©n√©ration hash pour user ID ${user.id}, saltRounds: ${saltRounds}`)
