// ATTENTION : Cette API est temporaire et doit √™tre s√©curis√©e ou supprim√©e apr√®s utilisation
export default async function handler(req, res) {
    // S√©curit√© : v√©rifier une cl√© secr√®te
    const { secret } = req.query
    
    if (secret !== 'init-aclef-2024') {
        return res.status(401).json({ error: 'Non autoris√©' })
    }

    if (req.method !== 'POST') {
        return res.status(405).json({ error: 'Method not allowed' })
    }

    try {
        // R√©cup√©rer tous les formateurs sans mot de passe hash√©
        const { data: formateurs, error: fetchError } = await supabase
            .from('users')
            .select('id, nom, prenom')
            .eq('role', 'formateur')
            .eq('archive', false)
            .or('password_hash.is.null,must_change_password.is.null')

        if (fetchError) {
            console.error('Erreur r√©cup√©ration formateurs:', fetchError)
            return res.status(500).json({ error: 'Erreur lors de la r√©cup√©ration des formateurs' })
        }

        const results = []
        
        for (const formateur of formateurs) {
            // Initialiser avec le nom comme mot de passe temporaire (non hash√©)
            // Le hash sera fait lors de la premi√®re connexion r√©ussie
            const { error: updateError } = await supabase
                .from('users')
                .update({
                    password_hash: formateur.nom, // Temporaire, sera compar√© en clair puis hash√©
                    must_change_password: true
                })
                .eq('id', formateur.id)

            if (updateError) {
                results.push({
                    formateur: `${formateur.prenom} ${formateur.nom}`,
                    status: 'erreur',
                    error: updateError.message
                })
            } else {
                results.push({
                    formateur: `${formateur.prenom} ${formateur.nom}`,
                    status: 'succ√®s',
                    login: formateur.prenom,
                    password: formateur.nom
                })
            }
        }

        return res.status(200).json({
            message: 'Initialisation termin√©e',
            formateurs_trait√©s: results.length,
            r√©sultats: results
        })

    } catch (error) {
        console.error('Erreur initialisation:', error)
        return res.status(500).json({ error: 'Erreur serveur' })
    }
}

