// Simulation du syst√®me admin_sessions en m√©moire
let adminSessions = {
  // Structure: { userId: { sessionId, isActive, sessionStart, lastHeartbeat, adminEmail } }
}

export default function handler(req, res) {
  const timestamp = new Date().toISOString()
  
  // üîß CORRECTION : G√©rer les deux formats (SendBeacon vs XHR)
  let body = req.body
  if (typeof req.body === 'string') {
    try {
      body = JSON.parse(req.body)
    } catch (e) {
      body = req.body
    }
  }
  
  if (req.method === 'POST') {
    const { action, userId, adminEmail, sessionId } = body
    
    try {
      switch(action) {
        case 'login':
          // V√©rifier si un admin est d√©j√† connect√©
          const activeSession = Object.values(adminSessions).find(session => session.isActive)
          
          if (activeSession) {
            console.log(`‚ùå LOGIN REFUS√â: ${adminEmail} - ${activeSession.adminEmail} d√©j√† connect√©`)
            return res.status(423).json({
              success: false,
              message: 'Un administrateur est d√©j√† connect√©',
              activeAdmin: activeSession.adminEmail,
              timestamp
            })
          }
          
          // Cr√©er nouvelle session
          adminSessions[userId] = {
            sessionId,
            isActive: true,
            sessionStart: timestamp,
            lastHeartbeat: timestamp,
            adminEmail
          }
          
          console.log(`‚úÖ LOGIN R√âUSSI: ${adminEmail} (${userId})`)
          return res.status(200).json({
            success: true,
            message: 'Connexion r√©ussie',
            sessionId,
            timestamp
          })
          
        case 'logout':
          // D√©connexion normale
          if (adminSessions[userId]) {
            adminSessions[userId].isActive = false
            console.log(`‚úÖ LOGOUT NORMAL: ${adminSessions[userId].adminEmail}`)
          }
          
          return res.status(200).json({
            success: true,
            message: 'D√©connexion r√©ussie'
          })
          
        case 'logout_emergency':
          // D√©connexion d'urgence (fermeture onglet)
          if (adminSessions[userId]) {
            const adminEmail = adminSessions[userId].adminEmail
            adminSessions[userId].isActive = false
            console.log(`üö® LOGOUT D'URGENCE: ${adminEmail} - Verrou lib√©r√© automatiquement`)
          }
          
          return res.status(200).json({
            success: true,
            message: 'D√©connexion urgence r√©ussie'
          })
          
        case 'heartbeat':
          // Maintenir la session active
          if (adminSessions[userId] && adminSessions[userId].isActive) {
            adminSessions[userId].lastHeartbeat = timestamp
            return res.status(200).json({ success: true })
          }
          
          return res.status(401).json({ success: false, message: 'Session invalide' })
          
        case 'check_status':
          // V√©rifier l'√©tat du verrou
          const currentActive = Object.entries(adminSessions).find(([id, session]) => session.isActive)
          
          return res.status(200).json({
            success: true,
            hasActiveSession: !!currentActive,
            activeAdmin: currentActive ? currentActive[1].adminEmail : null,
            allSessions: adminSessions,
            timestamp
          })
          
        case 'reset':
          // Reset complet pour les tests
          adminSessions = {}
          console.log('üîÑ RESET COMPLET: Toutes les sessions supprim√©es')
          
          return res.status(200).json({
            success: true,
            message: 'Reset complet effectu√©'
          })
          
        default:
          return res.status(400).json({
            success: false,
            message: 'Action inconnue'
          })
      }
      
    } catch (error) {
      console.error('‚ùå Erreur API verrou:', error)
      return res.status(500).json({
        success: false,
        message: 'Erreur serveur',
        error: error.message
      })
    }
  }
  
  if (req.method === 'GET') {
    // GET pour v√©rifier l'√©tat
    const activeSession = Object.values(adminSessions).find(session => session.isActive)
    
    return res.status(200).json({
      hasActiveSession: !!activeSession,
      activeAdmin: activeSession ? activeSession.adminEmail : null,
      allSessions: adminSessions
    })
  }
  
  return res.status(405).json({ message: 'M√©thode non autoris√©e' })
}

import { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import { supabase } from '../lib/supabaseClient'
import { withAuthAdmin } from '../components/withAuthAdmin'
import { logAudit, auditUserAction, getAuditStats, AUDIT_ACTIONS } from '../lib/auditLogger'

function Debug({ user, logout }) {
    const router = useRouter()
    
    // √âtats existants
    const [isConnected, setIsConnected] = useState(false)
    const [connectionMessage, setConnectionMessage] = useState('')
    const [isLoading, setIsLoading] = useState(false)
    const [message, setMessage] = useState('')
    
    // üìä NOUVEAUX √âTATS POUR LOGS AUDIT
    const [auditStats, setAuditStats] = useState(null)
    const [recentLogs, setRecentLogs] = useState([])
    const [testResult, setTestResult] = useState('')

    useEffect(() => {
        testConnection()
        loadAuditData() // Charger donn√©es audit
    }, [])

    // Fonction existante de test connexion
    const testConnection = async () => {
        try {
            const { data, error } = await supabase
                .from('users')
                .select('count(*)')
                .limit(1)

            if (error) {
                setConnectionMessage('‚ùå Erreur de connexion √† Supabase')
                setIsConnected(false)
            } else {
                setConnectionMessage('‚úÖ Connexion Supabase op√©rationnelle')
                setIsConnected(true)
            }
        } catch (error) {
            setConnectionMessage('‚ùå Erreur critique de connexion')
            setIsConnected(false)
        }
    }

    // üìä NOUVELLES FONCTIONS AUDIT
    const loadAuditData = async () => {
        try {
            // Charger les stats
            const stats = await getAuditStats()
            setAuditStats(stats)

            // Charger les 10 derniers logs
            const { data: logs } = await supabase
                .from('audit_logs')
                .select('*')
                .order('timestamp', { ascending: false })
                .limit(10)

            setRecentLogs(logs || [])
        } catch (error) {
            console.error('Erreur chargement audit:', error)
        }
    }

    // Test du syst√®me audit
    const testAuditSystem = async () => {
        setIsLoading(true)
        setTestResult('üß™ Test en cours...')
        
        try {
            // Test 1 : Log simple
            await logAudit(AUDIT_ACTIONS.LOGIN_ADMIN, {
                test: true,
                description: 'Test syst√®me audit depuis debug.js'
            })

            // Test 2 : Log action utilisateur
            await auditUserAction(AUDIT_ACTIONS.USER_MODIFIE, {
                id: 'test-id',
                prenom: 'Test',
                nom: 'Audit',
                role: 'test',
                avant: { status: 'ancien' },
                apres: { status: 'nouveau' }
            })

            // Recharger les donn√©es
            await loadAuditData()

            setTestResult('‚úÖ Test audit r√©ussi ! V√©rifiez les logs ci-dessous.')

        } catch (error) {
            setTestResult(`‚ùå Erreur test audit: ${error.message}`)
        } finally {
            setIsLoading(false)
        }
    }

