        borderRadius: '16px',
        padding: '16px',
        display: 'flex',
        flexDirection: 'column',
        height: '100%',
        maxHeight: '95vh'
    };

    return (
        <div style={containerStyle}>
            {/* ‚úÖ NOUVEAU: Modal pour message facultatif */}
            {showMessageModal && (
                <div style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    backgroundColor: 'rgba(0,0,0,0.5)',
