                        }}
                        onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'}
                        onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
                    >
                        D√©clarer mon planning type
                    </button>

                    <button
                        onClick={() => router.push('/formateur/mon-planning-type')}
                        style={{
                            backgroundColor: '#8b5cf6',
                            color: 'white',
                            padding: '20px 15px',
                            borderRadius: '15px',
                            border: 'none',
                            fontSize: '14px',
                            fontWeight: 'bold',
