                </div>
            </div>
        )
    }

    if (!user) {
        return null
    }

    return (
        <div style={{
            minHeight: '100vh',
            backgroundColor: '#ffffff',
            padding: '16px'
        }}>
            <div style={{
                maxWidth: '420px',
                margin: '0 auto',
                backgroundColor: 'white',
                borderRadius: '24px',
                padding: '20px'
            }}>
                {/* En-t√™te */}
                <div style={{ marginBottom: '16px', textAlign: 'center' }}>
                    <h1 style={{ 
                        fontSize: '20px', 
                        fontWeight: 'bold', 
                        margin: '0 0 6px 0',
                        color: '#1f2937'
                    }}>
                        Mon Planning Type
                    </h1>
                    <p style={{ 
                        fontSize: '14px', 
                        color: '#6b7280',
                        margin: '0'
                    }}>
                        Vos disponibilit√©s habituelles valid√©es
                    </p>
                </div>

                {/* Messages */}
                {message && (
                    <div style={{
                        padding: '12px',
                        borderRadius: '8px',
                        marginBottom: '16px',
                        backgroundColor: '#fee2e2',
                        color: '#991b1b',
                        fontSize: '14px',
                        textAlign: 'center'
                    }}>
                        {message}
                    </div>
                )}

                {/* Contenu principal */}
