import { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import { useFormateurAuth } from '../../contexts/FormateurAuthContext'
import { withAuthFormateur } from '../../components/withAuthFormateur'
import { supabase } from '../../lib/supabaseClient'

function ProfilFormateur() {
    const { user } = useFormateurAuth()
    const [email, setEmail] = useState('')
    const [newPassword, setNewPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [activeSection, setActiveSection] = useState('email')
    const [loading, setLoading] = useState(false)
    const [message, setMessage] = useState('')
    const [showPasswords, setShowPasswords] = useState(false)
    const router = useRouter()

    useEffect(() => {
        if (user) {
            setEmail(user.email || '')
        }
    }, [user])

    const handleUpdateEmail = async () => {
        
        // Si l'email est vide, on veut le supprimer
        if (email === '') {
            setLoading(true)
            try {
                const { error } = await supabase
                    .from('users')
                    .update({ email: null })
                    .eq('id', user.id)

                if (error) throw error

                setMessage('Email supprim√© avec succ√®s !')
                setTimeout(() => setMessage(''), 4000)
                
            } catch (error) {
                console.error('Erreur:', error)
                setMessage(`Erreur : ${error.message}`)
                setTimeout(() => setMessage(''), 4000)
            } finally {
                setLoading(false)
            }
            return
        }
        
        // Validation de l'email si non vide
        if (!email.trim()) {
            setMessage('Veuillez saisir un email valide')
            setTimeout(() => setMessage(''), 4000)
            return
        }

        if (!email.includes('@') || !email.includes('.')) {
            setMessage('Format d\'email invalide')
            setTimeout(() => setMessage(''), 4000)
            return
        }

        setLoading(true)
        try {
            const { error } = await supabase
                .from('users')
                .update({ email: email.trim() })
                .eq('id', user.id)

            if (error) throw error

            setMessage('Email modifi√© avec succ√®s !')
            setTimeout(() => setMessage(''), 4000)
            
        } catch (error) {
            console.error('Erreur:', error)
            setMessage(`Erreur : ${error.message}`)
            setTimeout(() => setMessage(''), 4000)
        } finally {
            setLoading(false)
        }
    }

    const handleUpdatePassword = async (e) => {
        e.preventDefault()
        
        if (!newPassword || !confirmPassword) {
            setMessage('Veuillez remplir tous les champs')
            setTimeout(() => setMessage(''), 4000)
            return
        }

        if (newPassword !== confirmPassword) {
            setMessage('Les nouveaux mots de passe ne correspondent pas')
            setTimeout(() => setMessage(''), 4000)
            return
        }

        if (newPassword.trim().length === 0) {
            setMessage('Le nouveau mot de passe ne peut pas √™tre vide')
            setTimeout(() => setMessage(''), 4000)
            return
        }

        setLoading(true)
        try {
            const token = localStorage.getItem('formateur_token')
            
            const response = await fetch('/api/formateur/update-password', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ newPassword })
