            if (response.ok) {
                setMessage('Mot de passe personnalis√© sauvegard√© !')
                setNewPassword('')
                setConfirmPassword('')
                setTimeout(() => setMessage(''), 4000)
            } else {
                setMessage(data.error || 'Erreur lors de la sauvegarde')
                setTimeout(() => setMessage(''), 4000)
