                        <p style={{ margin: 0, fontSize: '13px', color: '#6b7280' }}>
                            {user.email}
                        </p>
                    )}
                </div>

                {/* Navigation sections */}
                <div style={{ display: 'flex', gap: '8px', marginBottom: '24px' }}>
                    <button 
                        style={sectionButtonStyle(activeSection === 'email')}
                        onClick={() => setActiveSection('email')}
                    >
                        Email
                    </button>
                    <button 
                        style={sectionButtonStyle(activeSection === 'password')}
                        onClick={() => setActiveSection('password')}
                    >
                        Mot de passe
                    </button>
                </div>

                {/* Messages */}
                {message && (
                    <div style={{
                        padding: '12px',
                        borderRadius: '8px',
                        marginBottom: '16px',
                        backgroundColor: message.includes('succ√®s') ? '#d1fae5' : '#fee2e2',
                        color: message.includes('succ√®s') ? '#065f46' : '#991b1b',
                        fontSize: '14px',
                        fontWeight: 'bold',
                        whiteSpace: 'pre-line'
                    }}>
                        {message}
                    </div>
                )}

                {/* Section Email */}
                {activeSection === 'email' && (
                    <div>
                        <h3 style={{ margin: '0 0 16px 0', fontSize: '16px' }}>
                            G√©rer mes notifications email
                        </h3>
                        
                        {/* Option 1 : Recevoir les emails */}
                        <div 
                            onClick={() => setEmail(userData.email || '')}
                            style={{
                                padding: '15px',
                                marginBottom: '12px',
