import { useState, useEffect } from 'react'
import { useRouter } from 'next/router'
import { supabase } from '../lib/supabaseClient'
import { withAuthAdmin } from '../components/withAuthAdmin'

function GestionLieux({ user, logout, inactivityTime }) {
    const router = useRouter()
    
    // √âtats
    const [lieux, setLieux] = useState([])
    const [filtreStatut, setFiltreStatut] = useState('actif')
    const [isLoading, setIsLoading] = useState(false)
    const [message, setMessage] = useState('')
    
    // √âtats formulaire ajout
    const [showAjouterForm, setShowAjouterForm] = useState(false)
    const [nom, setNom] = useState('')
    const [initiale, setInitiale] = useState('')
    const [couleur, setCouleur] = useState('#3b82f6')
    
    // √âtats formulaire modification
    const [lieuEnModification, setLieuEnModification] = useState(null)
    const [showModifierForm, setShowModifierForm] = useState(false)
    
    // √âtats pour confirmation
    const [showConfirmation, setShowConfirmation] = useState(false)
    const [actionEnCours, setActionEnCours] = useState(null)

    // Couleurs pr√©d√©finies pour le s√©lecteur
    const couleursPredefinies = [
        '#ef4444', // Rouge
        '#f97316', // Orange
        '#f59e0b', // Ambre
        '#eab308', // Jaune
        '#84cc16', // Lime
        '#22c55e', // Vert
        '#10b981', // Emeraude
        '#14b8a6', // Teal
        '#06b6d4', // Cyan
        '#0ea5e9', // Bleu ciel
        '#3b82f6', // Bleu
        '#6366f1', // Indigo
        '#8b5cf6', // Violet
        '#a855f7', // Purple
        '#d946ef', // Fuchsia
        '#ec4899', // Pink
        '#f43f5e', // Rose
        '#6b7280'  // Gris
    ]

    useEffect(() => {
        fetchLieux()
    }, [filtreStatut])

    // Auto-g√©n√©ration de l'initiale
    useEffect(() => {
        if (nom) {
            const words = nom.split(' ')
            let initialeAuto = ''
            
            if (words.length >= 2) {
                // Si plusieurs mots, prendre la premi√®re lettre de chaque (max 3)
                initialeAuto = words.slice(0, 3).map(w => w.charAt(0)).join('').toUpperCase()
            } else {
                // Si un seul mot, prendre les 3 premi√®res lettres
                initialeAuto = nom.substring(0, 3).toUpperCase()
            }
            
            setInitiale(initialeAuto)
        }
    }, [nom])

    // Fonction pour r√©cup√©rer les lieux
    const fetchLieux = async () => {
        try {
            const { data, error } = await supabase
                .from('lieux')
                .select('*')
                .order('nom')

            if (error) throw error

            let lieuxFiltres = data || []
            
            if (filtreStatut === 'actif') {
                lieuxFiltres = data.filter(l => l.archive !== true)
            } else if (filtreStatut === 'archive') {
                lieuxFiltres = data.filter(l => l.archive === true)
            }

            setLieux(lieuxFiltres)
        } catch (error) {
            setMessage('Erreur lors du chargement des lieux')
            console.error(error)
        }
    }

    // Fonction pour ajouter un lieu
    const handleSubmitAjout = async (e) => {
        e.preventDefault()
        
        if (!nom.trim() || !initiale.trim()) {
            setMessage('Le nom et l\'initiale sont obligatoires')
            setTimeout(() => setMessage(''), 4000)
            return
        }

        setIsLoading(true)
        try {
            const { error } = await supabase.from('lieux').insert([{
                nom: nom.trim(),
                initiale: initiale.trim().toUpperCase(),
                couleur: couleur,
                archive: false
            }])
            
            if (error) throw error
            
            setMessage('Lieu ajout√© avec succ√®s !')
            setTimeout(() => setMessage(''), 4000)
            
            // R√©initialiser le formulaire
            setNom('')
            setInitiale('')
            setCouleur('#3b82f6')
            setShowAjouterForm(false)
            await fetchLieux()
            
        } catch (error) {
            setMessage(`Erreur : ${error.message}`)
            setTimeout(() => setMessage(''), 4000)
        } finally {
            setIsLoading(false)
        }
    }

    // Fonction pour modifier un lieu
    const handleSubmitModification = async (e) => {
        e.preventDefault()
        
        if (!lieuEnModification || !lieuEnModification.nom.trim() || !lieuEnModification.initiale.trim()) {
            setMessage('Le nom et l\'initiale sont obligatoires')
            setTimeout(() => setMessage(''), 4000)
            return
        }

        setIsLoading(true)
        try {
            const { error } = await supabase
                .from('lieux')
                .update({ 
                    nom: lieuEnModification.nom.trim(),
                    initiale: lieuEnModification.initiale.trim().toUpperCase(),
                    couleur: lieuEnModification.couleur
                })
                .eq('id', lieuEnModification.id)
            
            if (error) throw error
            
            setMessage('Lieu modifi√© avec succ√®s !')
            setTimeout(() => setMessage(''), 4000)
            setLieuEnModification(null)
            setShowModifierForm(false)
            await fetchLieux()
            
        } catch (error) {
            setMessage(`Erreur : ${error.message}`)
            setTimeout(() => setMessage(''), 4000)
        } finally {
            setIsLoading(false)
        }
    }

    // Initier la modification
    const initierModification = (lieu) => {
        setLieuEnModification({...lieu})
        setShowModifierForm(true)
        setShowAjouterForm(false)
    }

    // Actions archiver/d√©sarchiver/supprimer
    const initierAction = (lieu, typeAction) => {
        setActionEnCours({
            lieu: lieu,
            type: typeAction,
            message: getMessageConfirmation(lieu, typeAction)
        })
        setShowConfirmation(true)
    }

    const getMessageConfirmation = (lieu, typeAction) => {
        switch (typeAction) {
            case 'archiver':
                return `Archiver le lieu "${lieu.nom}" ?`
            case 'desarchiver':
                return `D√©sarchiver le lieu "${lieu.nom}" ?`
            case 'supprimer':
                return `Supprimer d√©finitivement le lieu "${lieu.nom}" ?\n\nATTENTION : Action irr√©versible !`
            default:
                return 'Confirmer cette action ?'
        }
    }

    const executerAction = async () => {
        if (!actionEnCours) return

        setIsLoading(true)
        try {
            const { lieu, type } = actionEnCours

            if (type === 'archiver') {
                await supabase.from('lieux').update({ archive: true }).eq('id', lieu.id)
                setMessage('Lieu archiv√© avec succ√®s !')
            } else if (type === 'desarchiver') {
                await supabase.from('lieux').update({ archive: false }).eq('id', lieu.id)
                setMessage('Lieu d√©sarchiv√© avec succ√®s !')
            } else if (type === 'supprimer') {
                await supabase.from('lieux').delete().eq('id', lieu.id)
                setMessage('Lieu supprim√© d√©finitivement !')
            }

            setTimeout(() => setMessage(''), 4000)
            await fetchLieux()

        } catch (error) {
            setMessage(`Erreur : ${error.message}`)
            setTimeout(() => setMessage(''), 4000)
        } finally {
            setIsLoading(false)
            setActionEnCours(null)
            setShowConfirmation(false)
        }
    }

    // Compter les lieux
    const compterLieux = () => {
        const actifs = lieux.filter(l => !l.archive).length
        const archives = lieux.filter(l => l.archive).length
        return { actifs, archives, total: lieux.length }
    }

    const stats = compterLieux()

    return (
        <div style={{
            minHeight: '100vh',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            padding: '40px 60px'
        }}>
            {/* Header avec navigation */}
            <div style={{
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
                borderRadius: '12px',
                padding: '8px 20px',
                marginBottom: '20px',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                backdropFilter: 'blur(10px)'
            }}>
                <nav style={{ fontSize: '14px' }}>
                    <span style={{ color: '#6b7280' }}>Dashboard</span>
                    <span style={{ margin: '0 10px', color: '#9ca3af' }}>/</span>
                    <span style={{ color: '#10b981', fontWeight: '500' }}>Gestion des Lieux</span>
                </nav>
                <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                    <button
                        onClick={() => router.push('/')}
                        style={{
                            padding: '8px 16px',
                            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                            color: 'white',
                            border: 'none',
                            borderRadius: '8px',
                            fontSize: '14px',
                            cursor: 'pointer'
                        }}
                    >
                        üè† Accueil
                    </button>
                    
                    <div style={{
                        padding: '6px 12px',
                        borderRadius: '8px',
                        fontSize: '12px',
                        fontWeight: '500',
                        display: 'flex',
                        alignItems: 'center',
                        gap: '6px',
                        backgroundColor: inactivityTime >= 240 ? '#fee2e2' : inactivityTime >= 180 ? '#fef3c7' : '#d1fae5',
                        color: inactivityTime >= 240 ? '#dc2626' : inactivityTime >= 180 ? '#d97706' : '#059669',
                        border: `1px solid ${inactivityTime >= 240 ? '#fca5a5' : inactivityTime >= 180 ? '#fcd34d' : '#6ee7b7'}`
                    }}>
                        Status : {inactivityTime >= 300 ? 'üò¥ ENDORMI!' : 
                                 inactivityTime >= 240 ? `‚ö†Ô∏è ${Math.floor((300 - inactivityTime) / 60)}m${(300 - inactivityTime) % 60}s` :
                                 inactivityTime >= 180 ? `‚è∞ ${Math.floor((300 - inactivityTime) / 60)}m${(300 - inactivityTime) % 60}s` :
                                 `üü¢ ACTIF`}
                    </div>
                    
                    <button
                        onClick={logout}
                        style={{
                            padding: '8px 16px',
                            background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
                            color: 'white',
                            border: 'none',
                            borderRadius: '8px',
                            fontSize: '14px',
                            cursor: 'pointer'
                        }}
                    >
                        üö™ D√©connexion
                    </button>
                </div>
            </div>

            {/* Titre principal */}
            <div style={{ textAlign: 'center', marginBottom: '30px' }}>
                <h1 style={{
                    fontSize: '36px',
                    fontWeight: 'bold',
                    color: 'white',
                    marginBottom: '10px'
                }}>
                    Gestion des Lieux
                </h1>
                <p style={{ color: 'rgba(255,255,255,0.8)', fontSize: '16px' }}>
                    G√©rez les lieux de formation avec leurs couleurs distinctives
                </p>
            </div>

            {/* Message de notification */}
            {message && (
                <div style={{
                    backgroundColor: message.includes('succ√®s') ? '#d1fae5' : '#fee2e2',
                    color: message.includes('succ√®s') ? '#065f46' : '#991b1b',
                    padding: '15px',
                    borderRadius: '8px',
                    marginBottom: '20px',
                    textAlign: 'center',
                    fontWeight: '500'
                }}>
                    {message}
                </div>
            )}

            {/* Bouton Ajouter */}
            <div style={{ marginBottom: '20px' }}>
                <button
                    onClick={() => {
                        setShowAjouterForm(!showAjouterForm)
                        setShowModifierForm(false)
                    }}
                    style={{
                        width: '100%',
                        padding: '15px',
                        background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                        color: 'white',
                        border: 'none',
                        borderRadius: '12px',
                        fontSize: '16px',
                        fontWeight: '600',
                        cursor: 'pointer',
                        transition: 'transform 0.2s'
                    }}
                    onMouseOver={(e) => e.target.style.transform = 'scale(1.02)'}
                    onMouseOut={(e) => e.target.style.transform = 'scale(1)'}
                >
                    üìç Ajouter un nouveau lieu
                </button>
            </div>

            {/* Formulaire d'ajout */}
            {showAjouterForm && (
                <div style={{
                    backgroundColor: 'white',
                    borderRadius: '12px',
                    padding: '20px',
                    marginBottom: '20px',
                    boxShadow: '0 10px 25px rgba(0,0,0,0.1)'
                }}>
                    <h3 style={{ marginBottom: '15px', color: '#1f2937' }}>Nouveau lieu</h3>
                    <div style={{
                        backgroundColor: '#f0f9ff',
                        border: '1px solid #0ea5e9',
                        borderRadius: '8px',
                        padding: '12px',
                        marginBottom: '15px'
                    }}>
                        <p style={{ margin: 0, fontSize: '14px', color: '#0284c7' }}>
                            <strong>üé® Gestion couleurs et initiales :</strong> Les initiales se g√©n√®rent automatiquement selon le nom (3 premi√®res lettres ou premi√®re lettre de chaque mot). Choisissez une couleur pour identifier facilement ce lieu dans les plannings.
                        </p>
                    </div>
                    <form onSubmit={handleSubmitAjout}>
                        <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr 200px', gap: '15px', marginBottom: '15px' }}>
                            <div>
                                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#6b7280' }}>
                                    Nom du lieu *
                                </label>
                                <input
                                    type="text"
                                    value={nom}
                                    onChange={(e) => setNom(e.target.value)}
                                    placeholder="Ex: Centre Camille Pag√©, Maison Pour Tous..."
                                    style={{
                                        width: '100%',
                                        padding: '10px',
                                        border: '1px solid #d1d5db',
                                        borderRadius: '8px',
                                        fontSize: '14px'
                                    }}
                                    required
                                />
                            </div>
                            <div>
                                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#6b7280' }}>
                                    Initiale (auto) *
                                </label>
                                <input
                                    type="text"
                                    value={initiale}
                                    onChange={(e) => setInitiale(e.target.value.substring(0, 3))}
                                    maxLength="3"
                                    style={{
                                        width: '100%',
                                        padding: '10px',
                                        border: '1px solid #d1d5db',
                                        borderRadius: '8px',
                                        fontSize: '14px',
                                        fontWeight: 'bold',
                                        textAlign: 'center',
                                        textTransform: 'uppercase'
                                    }}
                                    required
                                />
                            </div>
                            <div>
                                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#6b7280' }}>
                                    Couleur
                                </label>
                                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                                    <input
                                        type="color"
                                        value={couleur}
                                        onChange={(e) => setCouleur(e.target.value)}
                                        style={{
                                            width: '50px',
                                            height: '42px',
                                            border: '1px solid #d1d5db',
                                            borderRadius: '8px',
                                            cursor: 'pointer'
                                        }}
                                    />
                                    <div style={{
                                        padding: '10px 15px',
                                        backgroundColor: couleur,
                                        color: 'white',
                                        borderRadius: '8px',
                                        fontWeight: 'bold',
                                        flex: 1,
                                        textAlign: 'center',
                                        fontSize: '14px'
                                    }}>
                                        {initiale || 'ABC'}
                                    </div>
                                </div>
                            </div>
                        </div>
                        
                        {/* Palette de couleurs pr√©d√©finies */}
                        <div style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px', color: '#6b7280' }}>
                                Couleurs sugg√©r√©es :
                            </label>
                            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
                                {couleursPredefinies.map((c) => (
                                    <button
                                        key={c}
                                        type="button"
                                        onClick={() => setCouleur(c)}
                                        style={{
                                            width: '32px',
                                            height: '32px',
                                            backgroundColor: c,
                                            border: couleur === c ? '3px solid #1f2937' : '1px solid #d1d5db',
                                            borderRadius: '6px',
                                            cursor: 'pointer',
                                            transition: 'transform 0.2s'
                                        }}
                                        onMouseOver={(e) => e.target.style.transform = 'scale(1.2)'}
                                        onMouseOut={(e) => e.target.style.transform = 'scale(1)'}
                                    />
                                ))}
                            </div>
                        </div>

                        <div style={{ display: 'flex', gap: '10px' }}>
                            <button
                                type="submit"
                                disabled={isLoading}
                                style={{
                                    padding: '10px 20px',
                                    backgroundColor: isLoading ? '#9ca3af' : '#10b981',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    cursor: isLoading ? 'not-allowed' : 'pointer',
                                    fontWeight: '500'
                                }}
                            >
                                {isLoading ? 'Ajout...' : 'Ajouter'}
                            </button>
                            <button
                                type="button"
                                onClick={() => {
                                    setShowAjouterForm(false)
                                    setNom('')
                                    setInitiale('')
                                    setCouleur('#3b82f6')
                                }}
                                style={{
                                    padding: '10px 20px',
                                    backgroundColor: '#6b7280',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    cursor: 'pointer',
                                    fontWeight: '500'
                                }}
                            >
                                Annuler
                            </button>
                        </div>
                    </form>
                </div>
            )}

            {/* Formulaire de modification */}
            {showModifierForm && lieuEnModification && (
                <div style={{
                    backgroundColor: '#fef3c7',
                    borderRadius: '12px',
                    padding: '20px',
                    marginBottom: '20px',
                    border: '2px solid #f59e0b'
                }}>
                    <h3 style={{ marginBottom: '15px', color: '#92400e' }}>
                        üé® Modifier : {lieuEnModification.nom}
                    </h3>
                    <div style={{
                        backgroundColor: '#fbbf24',
                        color: '#92400e',
                        padding: '10px',
                        borderRadius: '6px',
                        marginBottom: '15px',
                        fontSize: '13px'
                    }}>
                        üîÑ <strong>Mise √† jour intelligente :</strong> Les initiales se recalculent automatiquement selon le nouveau nom.
                    </div>
                    <form onSubmit={handleSubmitModification}>
                        <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr 200px', gap: '15px', marginBottom: '15px' }}>
                            <div>
                                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#92400e' }}>
                                    Nom du lieu *
                                </label>
                                <input
                                    type="text"
                                    value={lieuEnModification.nom}
                                    onChange={(e) => {
                                        const newLieu = {...lieuEnModification, nom: e.target.value}
                                        const words = e.target.value.split(' ')
                                        let newInitiale = ''
                                        
                                        if (words.length >= 2) {
                                            newInitiale = words.slice(0, 3).map(w => w.charAt(0)).join('').toUpperCase()
                                        } else {
                                            newInitiale = e.target.value.substring(0, 3).toUpperCase()
                                        }
                                        
                                        setLieuEnModification({...newLieu, initiale: newInitiale})
                                    }}
                                    style={{
                                        width: '100%',
                                        padding: '10px',
                                        border: '1px solid #fbbf24',
                                        borderRadius: '8px',
                                        fontSize: '14px'
                                    }}
                                    required
                                />
                            </div>
                            <div>
                                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#92400e' }}>
                                    Initiale *
                                </label>
                                <input
                                    type="text"
                                    value={lieuEnModification.initiale}
                                    onChange={(e) => setLieuEnModification({
                                        ...lieuEnModification, 
                                        initiale: e.target.value.substring(0, 3)
                                    })}
                                    maxLength="3"
                                    style={{
                                        width: '100%',
                                        padding: '10px',
                                        border: '1px solid #fbbf24',
                                        borderRadius: '8px',
                                        fontSize: '14px',
                                        fontWeight: 'bold',
                                        textAlign: 'center',
                                        textTransform: 'uppercase'
                                    }}
                                    required
                                />
                            </div>
                            <div>
                                <label style={{ display: 'block', marginBottom: '5px', fontSize: '14px', color: '#92400e' }}>
                                    Couleur
                                </label>
                                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                                    <input
                                        type="color"
                                        value={lieuEnModification.couleur}
                                        onChange={(e) => setLieuEnModification({
                                            ...lieuEnModification,
                                            couleur: e.target.value
                                        })}
                                        style={{
                                            width: '50px',
                                            height: '42px',
                                            border: '1px solid #fbbf24',
                                            borderRadius: '8px',
                                            cursor: 'pointer'
                                        }}
                                    />
                                    <div style={{
                                        padding: '10px 15px',
                                        backgroundColor: lieuEnModification.couleur,
                                        color: 'white',
                                        borderRadius: '8px',
                                        fontWeight: 'bold',
                                        flex: 1,
                                        textAlign: 'center',
                                        fontSize: '14px'
                                    }}>
                                        {lieuEnModification.initiale}
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* Palette de couleurs pour modification */}
                        <div style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '8px', fontSize: '14px', color: '#92400e' }}>
                                Couleurs sugg√©r√©es :
                            </label>
                            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
                                {couleursPredefinies.map((c) => (
                                    <button
                                        key={c}
                                        type="button"
                                        onClick={() => setLieuEnModification({
                                            ...lieuEnModification,
                                            couleur: c
                                        })}
                                        style={{
                                            width: '32px',
                                            height: '32px',
                                            backgroundColor: c,
                                            border: lieuEnModification.couleur === c ? '3px solid #92400e' : '1px solid #fbbf24',
                                            borderRadius: '6px',
                                            cursor: 'pointer',
                                            transition: 'transform 0.2s'
                                        }}
                                        onMouseOver={(e) => e.target.style.transform = 'scale(1.2)'}
                                        onMouseOut={(e) => e.target.style.transform = 'scale(1)'}
                                    />
                                ))}
                            </div>
                        </div>

                        <div style={{ display: 'flex', gap: '10px' }}>
                            <button
                                type="submit"
                                disabled={isLoading}
                                style={{
                                    padding: '10px 20px',
                                    backgroundColor: isLoading ? '#9ca3af' : '#f59e0b',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    cursor: isLoading ? 'not-allowed' : 'pointer',
                                    fontWeight: '500'
                                }}
                            >
                                {isLoading ? 'Modification...' : 'Modifier'}
                            </button>
                            <button
                                type="button"
                                onClick={() => {
                                    setShowModifierForm(false)
                                    setLieuEnModification(null)
                                }}
                                style={{
                                    padding: '10px 20px',
                                    backgroundColor: '#6b7280',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    cursor: 'pointer',
                                    fontWeight: '500'
                                }}
                            >
                                Annuler
                            </button>
                        </div>
                    </form>
                </div>
            )}

            {/* Tableau avec filtres */}
            <div style={{
                backgroundColor: 'white',
                borderRadius: '12px',
                overflow: 'hidden',
                boxShadow: '0 10px 25px rgba(0,0,0,0.1)'
            }}>
                {/* Header avec filtres et stats */}
                <div style={{
                    padding: '20px',
                    backgroundColor: '#f9fafb',
                    borderBottom: '1px solid #e5e7eb',
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center'
                }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
                        <label style={{ fontWeight: '500', color: '#374151' }}>Afficher :</label>
                        <select
                            value={filtreStatut}
                            onChange={(e) => setFiltreStatut(e.target.value)}
                            style={{
                                padding: '8px 12px',
                                border: '1px solid #d1d5db',
                                borderRadius: '8px',
                                fontSize: '14px',
                                cursor: 'pointer'
                            }}
                        >
                            <option value="actif">Lieux actifs</option>
                            <option value="archive">Lieux archiv√©s</option>
                            <option value="tous">Tous les lieux</option>
                        </select>
                    </div>
                    
                    <div style={{ display: 'flex', gap: '20px', fontSize: '14px' }}>
                        <span style={{ 
                            padding: '5px 12px', 
                            backgroundColor: '#d1fae5', 
                            color: '#065f46',
                            borderRadius: '20px',
                            fontWeight: '500'
                        }}>
                            Actifs: {stats.actifs}
                        </span>
                        <span style={{ 
                            padding: '5px 12px', 
                            backgroundColor: '#f3f4f6', 
                            color: '#6b7280',
                            borderRadius: '20px',
                            fontWeight: '500'
                        }}>
                            Archiv√©s: {stats.archives}
                        </span>
                        <span style={{ 
                            padding: '5px 12px', 
                            backgroundColor: '#ede9fe', 
                            color: '#7c3aed',
                            borderRadius: '20px',
                            fontWeight: '500'
                        }}>
                            Total: {stats.total}
                        </span>
                    </div>
                </div>

                {/* Tableau */}
                {lieux.length > 0 ? (
                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                        <thead>
                            <tr style={{ backgroundColor: '#f9fafb' }}>
                                <th style={{ padding: '12px', textAlign: 'left', color: '#6b7280', fontWeight: '600', fontSize: '14px' }}>Statut</th>
                                <th style={{ padding: '12px', textAlign: 'left', color: '#6b7280', fontWeight: '600', fontSize: '14px' }}>Nom</th>
                                <th style={{ padding: '12px', textAlign: 'left', color: '#6b7280', fontWeight: '600', fontSize: '14px' }}>Initiale</th>
                                <th style={{ padding: '12px', textAlign: 'left', color: '#6b7280', fontWeight: '600', fontSize: '14px' }}>Couleur</th>
                                <th style={{ padding: '12px', textAlign: 'center', color: '#6b7280', fontWeight: '600', fontSize: '14px' }}>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {lieux.map((lieu) => (
                                <tr key={lieu.id} style={{ borderTop: '1px solid #e5e7eb' }}>
                                    <td style={{ padding: '12px' }}>
                                        <span style={{
                                            padding: '4px 12px',
                                            borderRadius: '20px',
                                            fontSize: '12px',
                                            fontWeight: '500',
                                            backgroundColor: lieu.archive ? '#f3f4f6' : '#d1fae5',
                                            color: lieu.archive ? '#6b7280' : '#065f46'
                                        }}>
                                            {lieu.archive ? 'üì¶ Archiv√©' : '‚úÖ Actif'}
                                        </span>
                                    </td>
                                    <td style={{ padding: '12px', fontWeight: '500' }}>{lieu.nom}</td>
                                    <td style={{ padding: '12px' }}>
                                        <span style={{
                                            padding: '6px 10px',
                                            backgroundColor: lieu.couleur || '#3b82f6',
                                            color: 'white',
                                            borderRadius: '8px',
                                            fontSize: '12px',
                                            fontWeight: 'bold'
                                        }}>
                                            {lieu.initiale}
                                        </span>
                                    </td>
                                    <td style={{ padding: '12px' }}>
                                        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                                            <div style={{
                                                width: '30px',
                                                height: '30px',
                                                backgroundColor: lieu.couleur || '#3b82f6',
                                                borderRadius: '6px',
                                                border: '1px solid #d1d5db'
                                            }} />
                                            <span style={{ fontSize: '12px', color: '#6b7280', fontFamily: 'monospace' }}>
                                                {lieu.couleur || '#3b82f6'}
                                            </span>
                                        </div>
                                    </td>
                                    <td style={{ padding: '12px', textAlign: 'center' }}>
                                        {lieu.archive ? (
                                            <div style={{ display: 'flex', gap: '8px', justifyContent: 'center' }}>
                                                <button
                                                    onClick={() => initierAction(lieu, 'desarchiver')}
                                                    style={{
                                                        padding: '6px 12px',
                                                        backgroundColor: '#10b981',
                                                        color: 'white',
                                                        border: 'none',
                                                        borderRadius: '6px',
                                                        fontSize: '12px',
                                                        cursor: 'pointer'
                                                    }}
                                                >
                                                    üì§ D√©sarchiver
                                                </button>
                                                <button
                                                    onClick={() => initierAction(lieu, 'supprimer')}
                                                    style={{
                                                        padding: '6px 12px',
                                                        backgroundColor: '#ef4444',
                                                        color: 'white',
                                                        border: 'none',
                                                        borderRadius: '6px',
                                                        fontSize: '12px',
                                                        cursor: 'pointer'
                                                    }}
                                                >
                                                    üóëÔ∏è Supprimer
                                                </button>
                                            </div>
                                        ) : (
                                            <div style={{ display: 'flex', gap: '8px', justifyContent: 'center' }}>
                                                <button
                                                    onClick={() => initierModification(lieu)}
                                                    style={{
                                                        padding: '6px 12px',
                                                        backgroundColor: '#3b82f6',
                                                        color: 'white',
                                                        border: 'none',
                                                        borderRadius: '6px',
                                                        fontSize: '12px',
                                                        cursor: 'pointer'
                                                    }}
                                                >
                                                    üé® Modifier
                                                </button>
                                                <button
                                                    onClick={() => initierAction(lieu, 'archiver')}
                                                    style={{
                                                        padding: '6px 12px',
                                                        backgroundColor: '#6b7280',
                                                        color: 'white',
                                                        border: 'none',
                                                        borderRadius: '6px',
                                                        fontSize: '12px',
                                                        cursor: 'pointer'
                                                    }}
                                                >
                                                    üì¶ Archiver
                                                </button>
                                            </div>
                                        )}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <div style={{ padding: '60px', textAlign: 'center' }}>
                        <p style={{ color: '#9ca3af', fontSize: '16px' }}>
                            {filtreStatut === 'actif' && 'Aucun lieu actif trouv√©.'}
                            {filtreStatut === 'archive' && 'Aucun lieu archiv√© trouv√©.'}
                            {filtreStatut === 'tous' && 'Aucun lieu trouv√©.'}
                        </p>
                    </div>
                )}
            </div>

            {/* Modal de confirmation */}
            {showConfirmation && actionEnCours && (
                <div style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    backgroundColor: 'rgba(0,0,0,0.5)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    zIndex: 1000
                }}>
                    <div style={{
                        backgroundColor: 'white',
                        borderRadius: '12px',
                        padding: '25px',
                        maxWidth: '400px',
                        width: '90%',
                        boxShadow: '0 20px 40px rgba(0,0,0,0.2)'
                    }}>
                        <h3 style={{ marginBottom: '15px', color: '#1f2937' }}>
                            Confirmer l'action
                        </h3>
                        <p style={{ marginBottom: '20px', color: '#6b7280', whiteSpace: 'pre-line' }}>
                            {actionEnCours.message}
                        </p>
                        <div style={{ display: 'flex', gap: '10px' }}>
                            <button
                                onClick={executerAction}
                                disabled={isLoading}
                                style={{
                                    flex: 1,
                                    padding: '10px',
                                    backgroundColor: actionEnCours.type === 'supprimer' ? '#ef4444' : '#3b82f6',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    fontWeight: '500',
                                    cursor: isLoading ? 'not-allowed' : 'pointer'
                                }}
                            >
                                {isLoading ? 'En cours...' : 'Confirmer'}
                            </button>
                            <button
                                onClick={() => {
                                    setShowConfirmation(false)
                                    setActionEnCours(null)
                                }}
                                disabled={isLoading}
                                style={{
                                    flex: 1,
                                    padding: '10px',
                                    backgroundColor: '#6b7280',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '8px',
                                    fontWeight: '500',
                                    cursor: isLoading ? 'not-allowed' : 'pointer'
                                }}
                            >
                                Annuler
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    )
}

