import { useEffect, useState } from 'react'
import { useRouter } from 'next/router'
import { supabase } from '../lib/supabaseClient'
import MessagerieSafeWrapper from '../components/MessagerieSafeWrapper'

// Composant Skeleton Loading pour anti-flickering
const SkeletonLoader = () => {
  const shimmer = {
    background: 'linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%)',
    backgroundSize: '200% 100%',
    animation: 'shimmer 1.5s infinite'
  }

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px'
    }}>
      <style jsx>{`
        @keyframes shimmer {
          0% { background-position: -200% 0; }
          100% { background-position: 200% 0; }
        }
      `}</style>
      
      {/* Header Skeleton */}
      <div style={{
        backgroundColor: 'rgba(255, 255, 255, 0.95)',
        borderRadius: '12px',
        padding: '20px',
        marginBottom: '20px',
        boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div style={{ flex: 1 }}>
          <div style={{ 
            height: '28px', 
            width: '300px', 
            borderRadius: '4px', 
            marginBottom: '10px',
            ...shimmer 
          }} />
          <div style={{ 
            height: '16px', 
            width: '200px', 
            borderRadius: '4px',
            ...shimmer 
          }} />
        </div>
        <div style={{ 
          height: '40px', 
          width: '120px', 
          borderRadius: '8px',
          ...shimmer 
        }} />
      </div>

      {/* Main Content Skeleton */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: '350px 1fr',
        gap: '20px',
        minHeight: 'calc(100vh - 200px)'
      }}>
        
        {/* Left Column Skeleton */}
        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
          {[1, 2].map(section => (
            <div key={section} style={{
              backgroundColor: 'rgba(255, 255, 255, 0.95)',
              borderRadius: '12px',
              padding: '20px',
              boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
              flex: 1
            }}>
              <div style={{ 
                height: '20px', 
                width: '120px', 
                borderRadius: '4px', 
                marginBottom: '15px',
                ...shimmer 
              }} />
              {[1, 2, 3, 4].map(item => (
                <div key={item} style={{ 
                  height: '48px', 
                  borderRadius: '8px', 
                  marginBottom: '10px',
                  ...shimmer 
                }} />
              ))}
            </div>
          ))}
        </div>

        {/* Right Column Skeleton (Messagerie) */}
        <div style={{
          backgroundColor: 'rgba(255, 255, 255, 0.95)',
          borderRadius: '12px',
          boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
          padding: '20px'
        }}>
          <div style={{ 
            height: '20px', 
            width: '150px', 
            borderRadius: '4px', 
            marginBottom: '20px',
            ...shimmer 
          }} />
          <div style={{ 
            height: '300px', 
            borderRadius: '8px', 
            marginBottom: '15px',
            ...shimmer 
          }} />
          <div style={{ 
            height: '40px', 
            borderRadius: '8px',
            ...shimmer 
          }} />
        </div>
      </div>
    </div>
  )
}

function Dashboard() {
  const [user, setUser] = useState(null)
  const [isLoading, setIsLoading] = useState(true)
  const [sessionValid, setSessionValid] = useState(false)
  const [inactivityTime, setInactivityTime] = useState(0) // Temps d'inactivit√© en secondes
  const [lastHeartbeat, setLastHeartbeat] = useState(null) // Dernier heartbeat
  const router = useRouter()

  const verifyAdminSession = async (supabaseUser) => {
    try {
      // V√©rifier la session dans la Table d'√âmeraude
      const { data: adminSession, error } = await supabase
        .from('admin_sessions')
        .select('*')
        .eq('admin_user_id', supabaseUser.id)
        .eq('is_active', true)
        .single()

      if (error || !adminSession) {
        console.warn('‚ùå Session admin non trouv√©e dans la Table d\'√âmeraude')
        return false
      }

      // V√©rifier si la session n'est pas expir√©e (exemple: 24h)
      const sessionStart = new Date(adminSession.session_start)
      const now = new Date()
      const hoursDiff = (now - sessionStart) / (1000 * 60 * 60)

      if (hoursDiff > 24) {
        console.warn('‚ùå Session expir√©e (plus de 24h)')
        // D√©sactiver la session expir√©e
        await supabase
          .from('admin_sessions')
          .update({ is_active: false })
          .eq('id', adminSession.id)
        return false
      }

      // Mettre √† jour le heartbeat
      await supabase
        .from('admin_sessions')
        .update({ heartbeat: new Date().toISOString() })
        .eq('id', adminSession.id)

      console.log('‚úÖ Session admin valide dans la Table d\'√âmeraude')
      return true

    } catch (error) {
      console.error('Erreur v√©rification session admin:', error)
      return false
    }
  }

  const checkAuthentication = async () => {
    try {
      setIsLoading(true)

      // Attendre la restauration automatique de session Supabase (important lors du refresh)
      await new Promise(resolve => setTimeout(resolve, 500))

      // 1. V√©rifier l'authentification Supabase
      const { data: { user: supabaseUser }, error } = await supabase.auth.getUser()

      if (error || !supabaseUser) {
        console.warn('‚ùå Pas d\'utilisateur Supabase authentifi√© apr√®s attente')
        router.push('/login')
        return
      }

      // 2. V√©rifier la session admin dans la Table d'√âmeraude
      const sessionIsValid = await verifyAdminSession(supabaseUser)
      
      if (!sessionIsValid) {
        console.warn('‚ùå Session admin invalide, redirection vers login')
        router.push('/login')
        return
      }

      // 3. Utilisateur valide - configurer l'√©tat
      setUser({
        id: supabaseUser.id,
        email: supabaseUser.email,
        role: 'admin',
        nom: 'Admin',
        prenom: 'ACLEF',
        dateConnexion: new Date().toISOString()
      })

      setSessionValid(true)
      console.log('‚úÖ Acc√®s autoris√© √† la premi√®re salle pour:', supabaseUser.email)

    } catch (error) {
      console.error('Erreur v√©rification authentification:', error)
      router.push('/login')
    } finally {
      setIsLoading(false)
    }
  }

  useEffect(() => {
    checkAuthentication()

    // üí° HEARTBEAT INTELLIGENT : Seulement sur activit√© r√©elle
    let lastActivity = Date.now()
    
    // D√©tecter l'activit√© utilisateur
    const resetActivity = () => {
      lastActivity = Date.now()
      setInactivityTime(0) // Reset du compteur
      console.log('üü¢ Activit√© d√©tect√©e, gardien √©veill√©')
    }
    
    // üö™ D√âCONNEXION FORC√âE √Ä LA FERMETURE (mais pas au refresh)
    const handlePageUnload = async (e) => {
      try {
        // Distinguer refresh (F5) vs fermeture r√©elle de l'onglet
        const isRefresh = e.persisted || (window.performance && window.performance.navigation.type === 1)
        
        if (isRefresh) {
          console.log('üîÑ Refresh d√©tect√© - Session pr√©serv√©e')
          return // Ne pas d√©sactiver la session sur refresh
        }
        
        console.log('üö™ Fermeture r√©elle d√©tect√©e - D√©connexion forc√©e...')
        
        const { data: { user: currentUser } } = await supabase.auth.getUser()
        if (currentUser) {
          // D√©sactiver la session seulement en cas de fermeture r√©elle
          await supabase
            .from('admin_sessions')
            .update({ is_active: false })
            .eq('admin_user_id', currentUser.id)
            .eq('is_active', true)
          
          console.log('‚úÖ Session d√©sactiv√©e √† la fermeture r√©elle')
        }
      } catch (error) {
        console.error('Erreur d√©connexion fermeture:', error)
      }
    }

    // √âcouter seulement la fermeture r√©elle (pas beforeunload qui se d√©clenche au refresh)
    window.addEventListener('unload', handlePageUnload)

    // √âv√©nements d'activit√©
    const activityEvents = ['mousedown', 'mousemove', 'keypress', 'scroll', 'touchstart', 'click']
    activityEvents.forEach(event => {
      document.addEventListener(event, resetActivity, true)
    })

    // COMPTEUR D'INACTIVIT√â (toutes les secondes)
    const inactivityCounter = setInterval(() => {
      const inactiveSeconds = Math.floor((Date.now() - lastActivity) / 1000)
      setInactivityTime(inactiveSeconds)
      
      if (inactiveSeconds >= 240) { // 240 secondes = 4 minutes (alerte avant 5min)
        console.log('üò¥ GARDIEN ENDORMI D√âTECT√â ! Expulsion imminente...')
      }
    }, 1000)

    // HEARTBEAT seulement si activit√© r√©cente
    const heartbeatInterval = setInterval(async () => {
      try {
        const { data: { user: currentUser } } = await supabase.auth.getUser()
        if (currentUser && sessionValid) {
          const inactiveTime = (Date.now() - lastActivity) / 1000 / 60 // minutes
          
          if (inactiveTime < 1) { // Actif dans la derni√®re minute
            const now = new Date().toISOString()
            await supabase
              .from('admin_sessions')
              .update({ heartbeat: now })
              .eq('admin_user_id', currentUser.id)
              .eq('is_active', true)
            
            setLastHeartbeat(now)
            console.log('üí° Heartbeat gardien actif envoy√©')
          } else {
            console.log('üò¥ Pas de heartbeat - gardien inactif depuis', inactiveTime.toFixed(1), 'minutes')
          }
        }
      } catch (error) {
        console.error('Erreur heartbeat:', error)
      }
    }, 30000) // 30 secondes

    // üò¥ EXPULSION DIRECTE : Bas√©e sur l'inactivit√© locale (toutes les 5 secondes)
    const surveillantInterval = setInterval(async () => {
      try {
        const { data: { user: currentUser } } = await supabase.auth.getUser()
        if (currentUser && sessionValid) {
          // V√âRIFICATION DIRECTE de l'inactivit√© locale (pas la DB !)
          const inactiveTime = (Date.now() - lastActivity) / 1000 / 60 // minutes
          
          if (inactiveTime > 5) { // üéØ 5 MINUTES
            console.log('üò¥ INACTIVIT√â LOCALE D√âTECT√âE ! Auto-expulsion en cours...')
            
            // Auto-expulsion
            await supabase
              .from('admin_sessions')
              .update({ is_active: false })
              .eq('admin_user_id', currentUser.id)
            
            // D√©connexion forc√©e
            await supabase.auth.signOut()
            
            alert('‚öîÔ∏è EXPULSION : Vous avez √©t√© d√©connect√© pour inactivit√© (5 minutes) !')
            router.push('/login')
          }
        }
      } catch (error) {
        console.error('Erreur surveillant:', error)
      }
    }, 5000) // 5 secondes

    // √âcouter les changements d'√©tat d'authentification
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        if (event === 'SIGNED_OUT' || !session) {
          console.log('üö™ D√©connexion d√©tect√©e')
          clearInterval(heartbeatInterval)
          clearInterval(surveillantInterval)
          router.push('/login')
        }
      }
    )

    return () => {
      subscription.unsubscribe()
      clearInterval(heartbeatInterval)
      clearInterval(surveillantInterval)
      clearInterval(inactivityCounter)
      
      // Nettoyer les √©v√©nements d'activit√©
      activityEvents.forEach(event => {
        document.removeEventListener(event, resetActivity, true)
      })
      
      // Nettoyer les √©v√©nements de fermeture
      window.removeEventListener('unload', handlePageUnload)
    }
  }, [router, sessionValid])

  const logout = async () => {
    try {
      // 1. D√©sactiver la session admin
      const { data: { user: currentUser } } = await supabase.auth.getUser()
      if (currentUser) {
        await supabase
          .from('admin_sessions')
          .update({ is_active: false })
          .eq('admin_user_id', currentUser.id)
          .eq('is_active', true)
      }

      // 2. D√©connexion Supabase
      await supabase.auth.signOut()

      // 3. Nettoyage localStorage (compatibility)
      localStorage.removeItem('admin_connecte')
      localStorage.removeItem('admin_session_token')

      console.log('‚úÖ D√©connexion compl√®te')

      // 4. Redirection
      router.push('/login')

    } catch (error) {
      console.error('Erreur d√©connexion:', error)
      // Forcer la redirection m√™me en cas d'erreur
      router.push('/login')
    }
  }

  // √âtat de chargement avec skeleton anti-flickering
  if (isLoading) {
    return <SkeletonLoader />
  }

  // √âtat de redirection avec skeleton pour √©viter flickering
  if (!user || !sessionValid) {
    return <SkeletonLoader />
  }

  const planningItems = [
    { title: "Planning Coordonnateur", icon: "üìÖ", href: "/planning-coordo" },
    { title: "Valider Changements", icon: "‚úÖ", href: "/valider-changements" },
    { title: "Prise Contr√¥le Formateur", icon: "üéÆ", href: "/prise-controle-formateur" },
    { title: "Valider Planning Type", icon: "üë®‚Äçüè´", href: "/planning-type-formateurs" }
  ]

  const gestionItems = [
    { title: "Gestion Formateurs", icon: "üë®‚Äçüè´", href: "/gestion-formateurs" },
    { title: "Gestion Apprenants", icon: "üë®‚Äçüéì", href: "/gestion-apprenants" },
    { title: "Gestion Salari√©s", icon: "üë∑‚Äç‚ôÇÔ∏è", href: "/gestion-salaries" },
    { title: "Gestion Lieux", icon: "üìç", href: "/gestion-lieux" }
  ]


  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '20px',
      opacity: 1,
      transition: 'opacity 0.3s ease-in-out'
    }}>
      <div style={{
        backgroundColor: 'rgba(255, 255, 255, 0.95)',
        borderRadius: '12px',
        padding: '20px',
        marginBottom: '20px',
        boxShadow: '0 10px 25px rgba(0,0,0,0.1)',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div>
          <h1 style={{
            fontSize: '28px',
            fontWeight: 'bold',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            margin: 0
          }}>
            ACLEF Planning Administration
          </h1>
          <p style={{ color: '#6b7280', marginTop: '5px' }}>
            Bienvenue {user?.email ? user.email.split('@')[0].charAt(0).toUpperCase() + user.email.split('@')[0].slice(1) : 'ACLEF Admin'}
            <span style={{ 
              marginLeft: '5px', 
              fontSize: '12px', 
              color: inactivityTime >= 240 ? '#dc2626' : inactivityTime >= 180 ? '#f59e0b' : '#10b981',
              backgroundColor: inactivityTime >= 240 ? '#fee2e2' : inactivityTime >= 180 ? '#fef3c7' : '#d1fae5',
              padding: '2px 6px',
              borderRadius: '4px',
              fontWeight: 'bold'
            }}>
              {inactivityTime >= 300 ? 'üò¥ ENDORMI!' : 
               inactivityTime >= 240 ? `‚ö†Ô∏è ${Math.floor((300 - inactivityTime) / 60)}m${(300 - inactivityTime) % 60}s` :
               inactivityTime >= 180 ? `‚è∞ ${Math.floor((300 - inactivityTime) / 60)}m${(300 - inactivityTime) % 60}s` :
               `üü¢ Actif`}
            </span>
          </p>
        </div>
        <button
          onClick={logout}
          style={{
            padding: '10px 20px',
            background: 'linear-gradient(135deg, #dc2626 0%, #991b1b 100%)',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontSize: '14px',
