            } else {
                errorMsg = 'Erreur de connexion. Veuillez r√©essayer.'
            }
            
            // Afficher seulement dans la zone d'erreur, pas en popup
            setErrorMessage(errorMsg)
        } finally {
            setIsLoading(false)
        }
    }

    return (
