                            {isLoading ? 'Validation...' : 'Valider & Transmettre'}
                        </button>
                    </div>
                </div>

                {message && (
                    <div className="no-print" style={{
                        backgroundColor: message.includes('ü§¥') ? '#dbeafe' : 
                                         message.includes('‚úÖ') ? '#d1fae5' :
                                         message.includes('üìù') ? '#fef3c7' : '#fee2e2',
                        color: message.includes('ü§¥') ? '#1e40af' : 
                               message.includes('‚úÖ') ? '#065f46' :
                               message.includes('üìù') ? '#92400e' : '#991b1b',
                        padding: '8px 15px',
                        borderRadius: '6px',
                        marginBottom: '10px',
                        marginLeft: '20px',
                        marginRight: '20px',
                        textAlign: 'center',
                        fontSize: '14px',
                        whiteSpace: 'pre-line'
                    }}>
                        {message}
                    </div>
                )}

                <div className="planning-container" style={{
                    backgroundColor: 'white',
                    borderRadius: '12px',
                    margin: '0 20px 20px 20px',
                    padding: '15px',
                    overflow: 'auto',
                    boxShadow: '0 10px 25px rgba(0,0,0,0.1)'
                }}>
                    <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                        <thead>
                            <tr style={{ backgroundColor: '#f9fafb' }}>
                                <th style={{ 
                                    padding: '10px', 
                                    border: '1px solid #e5e7eb',
                                    fontWeight: '600',
                                    fontSize: '13px',
                                    textAlign: 'center',
                                    minWidth: '40px',
                                    maxWidth: '40px'
                                }}>
                                </th>
                                {jours.map((jour, dayIndex) => 
                                    (lieuxParJour[dayIndex] || []).map((lieuIndex, lieuPos) => (
