                                                    {(lieuxParJour[dayIndex] || []).length > 1 && (
                                                        <button 
                                                            onClick={() => handleRemoveLieu(dayIndex, lieuIndex)}
                                                            style={{
                                                                padding: '3px 6px',
                                                                background: '#ef4444',
                                                                color: 'white',
                                                                border: 'none',
                                                                borderRadius: '3px',
                                                                fontSize: '11px',
                                                                cursor: 'pointer',
                                                                fontWeight: 'bold'
                                                            }}
                                                        >
                                                            ‚àí
                                                        </button>
                                                    )}
                                                </div>
                                            </div>
                                        </th>
                                    ))
                                )}
