                                                            ))}
                                                        </select>

                                                        <div style={{
                                                            padding: '6px',
                                                            background: 'rgba(255, 255, 255, 0.2)',
                                                            borderRadius: '4px',
                                                            border: '1px solid rgba(255, 255, 255, 0.3)'
                                                        }}>
                                                            <div className="formateur-header" style={{ 
                                                                fontSize: '10px', 
                                                                fontWeight: '600', 
                                                                marginBottom: '4px',
                                                                textAlign: 'center'
                                                            }}>
                                                                FORMATEURS
                                                            </div>
                                                            {(formateursParCase[cellKey] || [""]).map((selectedId, i) => {
                                                                const formateursDisponibles = getFormateursDisponibles(jour, creneau, selectedLieuId);
                                                                
                                                                return (
                                                                    <select
                                                                        key={i}
                                                                        style={{
                                                                            width: '100%',
                                                                            padding: '3px',
                                                                            border: '1px solid #d1d5db',
                                                                            borderRadius: '3px',
                                                                            fontSize: '10px',
                                                                            background: 'rgba(255,255,255,0.9)',
                                                                            color: '#374151',
                                                                            marginBottom: '3px'
                                                                        }}
                                                                        value={selectedId}
                                                                        onChange={(e) => handleFormateurChange(dayIndex, lieuIndex, creneau, i, e.target.value)}
                                                                    >
                                                                        <option value="">Formateur</option>
                                                                        {formateursDisponibles
                                                                            .filter(f => {
                                                                                const currentSelections = formateursParCase[cellKey] || [];
                                                                                return !currentSelections.includes(f.id) || currentSelections[i] === f.id;
                                                                            })
                                                                            .map(f => (
                                                                                <option 
                                                                                    key={f.id} 
                                                                                    value={f.id}
                                                                                    style={{
                                                                                        backgroundColor: f.statut === 'disponible' ? '#dbeafe' : 
                                                                                                      f.statut === 'dispo_except' ? '#fef3c7' : 'white',
                                                                                        color: f.statut === 'disponible' ? '#1e40af' : 
                                                                                               f.statut === 'dispo_except' ? '#92400e' : '#374151'
                                                                                    }}
                                                                                >
                                                                                    {f.prenom} {f.nom}
                                                                                    {f.statut === 'dispo_except' ? ' (EXCEPT)' : 
                                                                                     f.statut === 'disponible' ? 
                                                                                        (f.lieuSpecifique ? ' (LIEU)' : ' (S/P)') : ''}
                                                                                    {f.source === 'exception_validee_roi' ? ' [ROI]' : ''}
                                                                                </option>
                                                                            ))}
                                                                    </select>
                                                                );
                                                            })}

                                                            <div className="no-print" style={{ display: 'flex', gap: '3px', justifyContent: 'center' }}>
                                                                <button
                                                                    onClick={() => handleAddFormateur(dayIndex, lieuIndex, creneau)}
                                                                    style={{
                                                                        padding: '3px 6px',
                                                                        background: '#3b82f6',
                                                                        color: 'white',
                                                                        border: 'none',
                                                                        borderRadius: '3px',
                                                                        fontSize: '10px',
                                                                        cursor: 'pointer',
                                                                        fontWeight: 'bold'
                                                                    }}
                                                                >
                                                                    +
                                                                </button>
                                                                {(formateursParCase[cellKey] || []).length > 1 && (
                                                                    <button
                                                                        onClick={() => handleRemoveFormateur(dayIndex, lieuIndex, creneau)}
                                                                        style={{
                                                                            padding: '3px 6px',
                                                                            background: '#ef4444',
                                                                            color: 'white',
                                                                            border: 'none',
                                                                            borderRadius: '3px',
                                                                            fontSize: '10px',
                                                                            cursor: 'pointer',
                                                                            fontWeight: 'bold'
                                                                        }}
                                                                    >
                                                                        ‚àí
                                                                    </button>
                                                                )}
                                                            </div>
                                                        </div>

                                                        <div style={{
                                                            padding: '6px',
                                                            background: 'rgba(255, 255, 255, 0.2)',
                                                            borderRadius: '4px',
                                                            border: '1px solid rgba(255, 255, 255, 0.3)'
                                                        }}>
                                                            <div style={{ 
                                                                fontSize: '10px', 
                                                                fontWeight: '600', 
                                                                marginBottom: '4px',
                                                                textAlign: 'center'
                                                            }}>
                                                                APPRENANTS
                                                            </div>
                                                            {(apprenantsParCase[cellKey] || [""]).map((selectedId, i) => (
                                                                <select
                                                                    key={i}
                                                                    style={{
                                                                        width: '100%',
                                                                        padding: '3px',
                                                                        border: '1px solid #d1d5db',
                                                                        borderRadius: '3px',
                                                                        fontSize: '10px',
                                                                        background: 'rgba(255,255,255,0.9)',
                                                                        color: '#374151',
                                                                        marginBottom: '3px'
                                                                    }}
                                                                    value={selectedId}
                                                                    onChange={(e) => handleApprenantChange(dayIndex, lieuIndex, creneau, i, e.target.value)}
                                                                >
                                                                    <option value="">Apprenant</option>
                                                                    {apprenants
                                                                        .filter(a => {
                                                                            const currentSelections = apprenantsParCase[cellKey] || [];
                                                                            return !currentSelections.includes(a.id) || currentSelections[i] === a.id;
                                                                        })
                                                                        .map(a => (
                                                                            <option key={a.id} value={a.id}>
                                                                                {a.prenom} {a.nom}
                                                                            </option>
                                                                        ))}
                                                                </select>
                                                            ))}

                                                            <div className="no-print" style={{ display: 'flex', gap: '3px', justifyContent: 'center' }}>
                                                                <button
                                                                    onClick={() => handleAddApprenant(dayIndex, lieuIndex, creneau)}
                                                                    style={{
                                                                        padding: '3px 6px',
                                                                        background: '#3b82f6',
                                                                        color: 'white',
                                                                        border: 'none',
                                                                        borderRadius: '3px',
                                                                        fontSize: '10px',
                                                                        cursor: 'pointer',
                                                                        fontWeight: 'bold'
                                                                    }}
                                                                >
                                                                    +
                                                                </button>
                                                                {(apprenantsParCase[cellKey] || []).length > 1 && (
                                                                    <button
                                                                        onClick={() => handleRemoveApprenant(dayIndex, lieuIndex, creneau)}
                                                                        style={{
                                                                            padding: '3px 6px',
                                                                            background: '#ef4444',
                                                                            color: 'white',
                                                                            border: 'none',
                                                                            borderRadius: '3px',
                                                                            fontSize: '10px',
                                                                            cursor: 'pointer',
                                                                            fontWeight: 'bold'
                                                                        }}
                                                                    >
                                                                        ‚àí
                                                                    </button>
                                                                )}
                                                            </div>
                                                        </div>
                                                    </div>
                                                </td>
                                            );
                                        })
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    )
}

