                        border: 1px solid #ccc !important;
                        background: #f5f5f5 !important;
                        line-height: 1.1 !important;
                        max-width: 80px !important; /* Largeur max par colonne */
                        overflow: hidden !important;
                        word-wrap: break-word !important;
                    }
                    
                    /* ‚úÖ CELLULES CONTENU OPTIMIS√âES */
                    td {
                        padding: 2px 1px !important;
                        font-size: 6px !important;
                        border: 1px solid #ccc !important;
                        vertical-align: top !important;
                        max-width: 80px !important;
                        overflow: hidden !important;
                        line-height: 1.1 !important;
                    }
                    
                    /* ‚≠ê NOUVEAU : MASQUER SELECTS EN IMPRESSION */
                    select {
                        display: none !important;
                    }
                    
                    /* ‚≠ê NOUVEAU : AFFICHER VERSIONS TEXTE POUR IMPRESSION */
                    .print-only {
                        display: block !important;
                    }
                    
                    /* ‚≠ê NOUVEAU : STYLES POUR TEXTES D'IMPRESSION */
                    .print-text {
                        font-size: 6px !important;
                        padding: 1px 2px !important;
                        background: #f8f9fa !important;
                        border: 1px solid #ccc !important;
                        border-radius: 2px !important;
                        margin-bottom: 1px !important;
                        min-height: 10px !important;
                        color: #333 !important;
                        font-weight: 500 !important;
                    }
                    
                    .print-text-empty {
                        color: #999 !important;
                        font-style: italic !important;
                    }
                    
                    /* ‚úÖ SECTIONS FORMATEURS/APPRENANTS COMPACTES */
                    div[style*="padding: '6px'"] {
                        padding: 2px 1px !important;
                        margin-bottom: 1px !important;
                        border: 1px solid #ddd !important;
                        border-radius: 2px !important;
                    }
                    
                    /* ‚úÖ TITRES SECTIONS R√âDUITS */
                    div[style*="fontSize: '10px'"][style*="fontWeight: '600'"] {
