                                                            onApprenantChange={handleApprenantChange}
                                                            onAddApprenant={handleAddApprenant}
                                                            onRemoveApprenant={handleRemoveApprenant}
                                                            disabled={!selectedLieuId}
                                                            couleurEnregistree={couleursEnregistrees[cellKey]}
                                                        />
                                                    </div>
                                                </td>
                                            );
                                        })
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    )
}

