// ==========================================
// PLANNING-TEST-V1.JS - APPROCHE COMPOSANT S√âPAR√â
// ==========================================
// Principe : MenuApprenants.js externe + int√©gration minimale
// Avantages : S√©paration des responsabilit√©s, r√©utilisabilit√©
// Inconv√©nients : Communication entre composants, complexit√©

import React, { useEffect, useState } from 'react';
import { supabase } from '../lib/supabaseClient';
import { useRouter } from 'next/router';
import { withAuthAdmin } from '../components/withAuthAdmin';
import MenuApprenants from '../components/MenuApprenants';

const jours = ['Lundi', 'Mardi', 'Mercredi', 'Jeudi', 'Vendredi'];

function PlanningTestV1({ user, logout, inactivityTime }) {
    const router = useRouter();
    
    // √âtats pour les donn√©es de base (identiques √† l'original)
    const [salaries, setSalaries] = useState([]);
    const [formateurs, setFormateurs] = useState([]);
    const [apprenants, setApprenants] = useState([]);
    const [currentDate, setCurrentDate] = useState(new Date());
    const [lieux, setLieux] = useState([]);
    const [planningTypes, setPlanningTypes] = useState([]);
    const [absencesValidees, setAbsencesValidees] = useState([]);
    
    // √âtats pour les donn√©es du planning (identiques √† l'original)
    const [apprenantsParCase, setApprenantsParCase] = useState({});
    const [formateursParCase, setFormateursParCase] = useState({});
    const [lieuxParJour, setLieuxParJour] = useState({});
    const [lieuxSelectionnes, setLieuxSelectionnes] = useState({});
    const [salariesSelectionnes, setSalariesSelectionnes] = useState({});
    
    // √âtats de contr√¥le
    const [isLoading, setIsLoading] = useState(false);
    const [dataLoaded, setDataLoaded] = useState(false);
    const [message, setMessage] = useState('');
    const [filtreDisponibilite, setFiltreDisponibilite] = useState('toutes');

    // NOUVEAUX √âTATS POUR APPROCHE V1 - COMPOSANT S√âPAR√â
    const [apprenantsStats, setApprenantsStats] = useState({
        totalCases: 0,
        casesAvecApprenants: 0,
        totalApprenants: 0
    });

    // Fonctions utilitaires (copi√©es de l'original)
    function getDateOfWeek(dayIndex, currentDate) {
        const startOfWeek = new Date(currentDate);
        startOfWeek.setDate(currentDate.getDate() - currentDate.getDay() + 1);
        const resultDate = new Date(startOfWeek);
        resultDate.setDate(startOfWeek.getDate() + dayIndex);
        return resultDate.toLocaleDateString('fr-FR');
    }

    function getCurrentWeekStart() {
        const now = new Date();
        const startOfWeek = new Date(now);
        startOfWeek.setDate(now.getDate() - now.getDay() + 1);
        return startOfWeek;
    }

    function getWeekDates(currentDate) {
        const startOfWeek = new Date(currentDate);
        startOfWeek.setDate(currentDate.getDate() - currentDate.getDay() + 1);
        
        const weekDates = [];
        for (let i = 0; i < 5; i++) {
            const date = new Date(startOfWeek);
            date.setDate(startOfWeek.getDate() + i);
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0');
            const day = String(date.getDate()).padStart(2, '0');
            weekDates.push(`${year}-${month}-${day}`);
        }
        return weekDates;
    }

    const semaine = Math.ceil(((currentDate - new Date(currentDate.getFullYear(), 0, 1)) / 86400000 + new Date(currentDate.getFullYear(), 0, 1).getDay() + 1) / 7);

    // Helper functions (copi√©es de l'original)
    const getNomLieu = (lieuId) => {
        if (!lieuId) return '';
        const lieu = lieux.find(l => l.id === lieuId);
        return lieu ? lieu.nom : '';
    };

    const getNomSalarie = (salarieId) => {
        if (!salarieId) return '';
        const salarie = salaries.find(s => s.id === salarieId);
        return salarie ? (salarie.initiales || getInitiales(salarie.prenom, salarie.nom)) : '';
    };

    const getNomFormateur = (formateurId) => {
        if (!formateurId) return '';
        const formateur = formateurs.find(f => f.id === formateurId);
        return formateur ? `${formateur.prenom} ${formateur.nom}` : '';
    };

    const getNomApprenant = (apprenantId) => {
        if (!apprenantId) return '';
        const apprenant = apprenants.find(a => a.id === apprenantId);
        return apprenant ? `${apprenant.prenom} ${apprenant.nom}` : '';
    };

    const getLieuCouleur = (lieuId) => {
        if (!lieuId) return '#ffffff';
        const lieu = lieux.find(l => l.id === lieuId);
        return lieu?.couleur || '#ffffff';
    };

    const getTextColor = (backgroundColor) => {
        if (!backgroundColor || backgroundColor === '#ffffff') return '#000000';
        
        const hex = backgroundColor.replace('#', '');
        const r = parseInt(hex.substr(0, 2), 16);
        const g = parseInt(hex.substr(2, 2), 16);
        const b = parseInt(hex.substr(4, 2), 16);
        
        const luminance = (0.299 * r + 0.587 * g + 0.114 * b) / 255;
        return luminance > 0.5 ? '#000000' : '#ffffff';
    };

    const getInitiales = (prenom, nom) => {
        return `${prenom?.[0] ?? ''}${nom?.[0] ?? ''}`.toUpperCase();
    };

    // Fonction de filtrage formateurs (copi√©e et simplifi√©e de l'original)
    const getFormateursDisponibles = (jour, creneau, lieuId) => {
        const creneauDB = creneau === 'Matin' ? 'Matin' : 'AM';
        
        if (!jour || !creneau) {
            return formateurs.map(f => ({ ...f, statut: null }));
        }

        // Version simplifi√©e pour les tests
        return formateurs.map(f => ({ 
            ...f, 
            statut: 'disponible',
            lieuSpecifique: false,
            source: 'test_v1'
        }));
    };

    // ‚≠ê FONCTION CENTRALE V1 - GESTION APPRENANTS VIA COMPOSANT EXTERNE
    const handleApprenantSelect = (caseKey, apprenantId, action = 'add') => {
        console.log(`üéØ V1 - ${action} apprenant ${apprenantId} dans case ${caseKey}`);
        
        setApprenantsParCase(prev => {
            const currentList = prev[caseKey] || [''];
            let newList;
            
            if (action === 'remove') {
                // Retirer l'apprenant de la liste
                newList = currentList.filter(id => id !== apprenantId);
                // S'assurer qu'il reste au moins un slot vide
                if (newList.length === 0 || !newList.includes('')) {
                    newList.push('');
                }
            } else {
                // Ajouter l'apprenant
                const emptyIndex = currentList.findIndex(id => id === '');
                if (emptyIndex !== -1) {
                    newList = [...currentList];
                    newList[emptyIndex] = apprenantId;
                    // Ajouter un nouveau slot vide si n√©cessaire
                    if (!newList.includes('')) {
                        newList.push('');
                    }
                } else {
                    newList = [...currentList.filter(id => id), apprenantId, ''];
                }
            }
            
            return { ...prev, [caseKey]: newList };
        });

        // Mettre √† jour les statistiques
        updateApprenantsStats();
        
        // Afficher message de feedback
        const apprenantNom = getNomApprenant(apprenantId) || apprenantId;
        const actionText = action === 'remove' ? 'retir√© de' : 'ajout√© √†';
        setMessage(`üë• V1: ${apprenantNom} ${actionText} la case ${caseKey}`);
        setTimeout(() => setMessage(''), 3000);
    };

    // Fonction pour obtenir les donn√©es de cr√©neau pour MenuApprenants
    const getCreneauDataForMenu = (dayIndex, lieuIndex, creneau) => {
        const weekDates = getWeekDates(currentDate);
        const caseKey = `${dayIndex}-${lieuIndex}-${creneau}`;
        
        return {
            date: weekDates[dayIndex],
            jour: jours[dayIndex],
            creneau: creneau,
            lieu_id: lieuxSelectionnes[caseKey] || null
        };
    };

    // Fonction pour obtenir les apprenants s√©lectionn√©s pour une case
    const getSelectedApprenantsForCase = (caseKey) => {
        const selectedList = apprenantsParCase[caseKey] || [''];
        return selectedList.filter(id => id && id !== '');
    };

    // Mettre √† jour les statistiques d'apprenants
    const updateApprenantsStats = () => {
        let totalCases = 0;
        let casesAvecApprenants = 0;
        let totalApprenants = 0;

        Object.entries(apprenantsParCase).forEach(([key, list]) => {
            totalCases++;
            const apprenantsDansCase = (list || []).filter(id => id && id !== '');
            if (apprenantsDansCase.length > 0) {
                casesAvecApprenants++;
                totalApprenants += apprenantsDansCase.length;
            }
        });

        setApprenantsStats({
            totalCases,
            casesAvecApprenants,
            totalApprenants
        });
    };

    // R√âCUP√âRATION DES DONN√âES DE BASE (identique √† l'original)
    useEffect(() => {
        const fetchData = async () => {
            try {
                const [salariesRes, formateursRes, apprenantsRes, lieuxRes] = await Promise.all([
                    supabase.from('users').select('id, prenom, nom, role, initiales').eq('role', 'salari√©').eq('archive', false),
                    supabase.from('users').select('id, prenom, nom, role').eq('role', 'formateur').eq('archive', false),
                    supabase.from('users').select('id, prenom, nom, role').eq('role', 'apprenant').eq('archive', false),
                    supabase.from('lieux').select('id, nom, couleur, initiale').eq('archive', false)
                ]);

                if (salariesRes.data) setSalaries(salariesRes.data);
                if (formateursRes.data) setFormateurs(formateursRes.data);
                if (apprenantsRes.data) setApprenants(apprenantsRes.data);
                if (lieuxRes.data) setLieux(lieuxRes.data);
                
            } catch (error) {
                console.error('Erreur lors du chargement des donn√©es V1:', error);
            }
        };
        
        fetchData();
    }, []);

    // CHARGEMENT DU PLANNING (simplifi√© pour les tests)
    const loadWeekPlanning = async (targetDate) => {
        try {
            // Initialisation simple pour les tests
            const newApprenantsParCase = {};
            const newFormateursParCase = {};
            const newLieuxSelectionnes = {};
            const newSalariesSelectionnes = {};
