            const emptyIndex = currentList.findIndex(id => id === '');
            
            let newList;
            if (emptyIndex !== -1) {
                newList = [...currentList];
                newList[emptyIndex] = apprenantId;
                if (!newList.includes('')) {
                    newList.push('');
                }
            } else {
                newList = [...currentList.filter(id => id), apprenantId, ''];
            }
            
            return {
                ...prev,
                [caseKey]: {
                    ...prev[caseKey],
                    apprenants_ids: newList
                }
            };
        });

        const apprenant = apprenantsMap.get(apprenantId);
        const apprenantNom = apprenant ? apprenant.prenom : apprenantId;
        setMessage(`üë• V3: ${apprenantNom} ajout√© avec cache optimis√© √† ${caseKey}`);
