            const lieu = lieux.find(l => l.id === lieuId)
            setPlanningType(prev => ({
                ...prev,
                [key]: {
                    lieu_id: lieuId,
                    lieu_nom: lieu?.nom,
                    lieu_couleur: lieu?.couleur,
                    lieu_initiale: lieu?.initiale
                }
            }))
        } else {
