                        <option key={lieu.id} value={lieu.id}>
                            {lieu.initiale || lieu.nom}
                        </option>
                    ))}
                </select>
            </td>
        )
    }

    // Statistiques du planning
    const nbCreneaux = Object.keys(planningType).length

    return (
        <>
            {/* Styles CSS int√©gr√©s */}
            <style jsx>{`
                .planning-type-apprenants {
                    min-height: 100vh;
                    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
                    padding: 20px;
                }

                .container-planning {
                    max-width: 1200px;
                    margin: 0 auto;
                    background: rgba(255, 255, 255, 0.1);
                    backdrop-filter: blur(20px);
                    border-radius: 20px;
                    border: 1px solid rgba(255, 255, 255, 0.2);
                    padding: 30px;
                }

                .header-title {
                    color: white;
                    font-size: 28px;
                    font-weight: bold;
                    text-align: center;
                    margin-bottom: 30px;
                    text-shadow: 0 2px 4px rgba(0,0,0,0.3);
                }

                .navigation {
                    background: rgba(255, 255, 255, 0.95);
                    border-radius: 12px;
                    padding: 8px 20px;
                    margin-bottom: 20px;
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                }

                .selecteur-section {
                    background: rgba(255, 255, 255, 0.1);
                    border-radius: 15px;
                    padding: 25px;
                    margin-bottom: 30px;
                    border: 1px solid rgba(255, 255, 255, 0.2);
                }

                .selecteur-label {
                    color: white;
                    font-size: 18px;
                    font-weight: bold;
                    margin-bottom: 15px;
                    display: block;
                }

                .select-apprenant {
                    width: 100%;
                    max-width: 400px;
                    padding: 12px 16px;
                    border: none;
                    border-radius: 10px;
                    background: rgba(255, 255, 255, 0.95);
                    font-size: 16px;
                    font-weight: 500;
                    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
                    cursor: pointer;
                }

                .grille-container {
                    background: rgba(255, 255, 255, 0.1);
                    border-radius: 15px;
                    padding: 20px;
                    margin: 20px 0;
                    border: 1px solid rgba(255, 255, 255, 0.2);
                }

                .grille-planning {
                    width: 100%;
                    border-collapse: collapse;
                    background: rgba(255, 255, 255, 0.05);
                    border-radius: 15px;
                    overflow: hidden;
                    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
                }

                .header-jour, .header-creneau {
                    background: rgba(0, 0, 0, 0.3);
                    color: white;
                    padding: 15px;
                    font-weight: bold;
                    text-align: center;
                    font-size: 14px;
                    border: 1px solid rgba(255, 255, 255, 0.1);
                }

                .label-creneau {
                    background: rgba(0, 0, 0, 0.2);
                    color: white;
                    padding: 15px;
                    font-weight: bold;
                    text-align: center;
                    width: 100px;
                    font-size: 13px;
                    border: 1px solid rgba(255, 255, 255, 0.1);
                }

                .case-planning {
                    padding: 8px;
                    border: 1px solid rgba(255, 255, 255, 0.2);
                    text-align: center;
                    background: rgba(255, 255, 255, 0.05);
                    transition: background-color 0.3s ease;
                }

                .case-planning:hover {
                    background: rgba(255, 255, 255, 0.1);
                }

                .select-lieu {
                    width: 100%;
                    padding: 8px;
                    border: none;
                    border-radius: 6px;
                    text-align: center;
                    font-size: 13px;
                    cursor: pointer;
                    transition: all 0.3s ease;
                }

                .select-lieu:disabled {
                    opacity: 0.5;
                    cursor: not-allowed;
                }

                .actions-section {
                    background: rgba(255, 255, 255, 0.1);
                    border-radius: 15px;
                    padding: 25px;
                    text-align: center;
                    border: 1px solid rgba(255, 255, 255, 0.2);
                }

                .btn-sauvegarder {
                    background: linear-gradient(135deg, #4CAF50, #45a049);
                    color: white;
                    border: none;
                    padding: 15px 30px;
                    border-radius: 10px;
                    font-size: 16px;
                    font-weight: bold;
                    cursor: pointer;
                    transition: transform 0.2s ease;
                    box-shadow: 0 4px 15px rgba(76, 175, 80, 0.3);
                }

                .btn-sauvegarder:hover:not(:disabled) {
                    transform: translateY(-2px);
                    box-shadow: 0 6px 20px rgba(76, 175, 80, 0.4);
                }

                .btn-sauvegarder:disabled {
                    opacity: 0.5;
                    cursor: not-allowed;
                    transform: none;
                }

                .stats-planning {
                    margin-top: 15px;
                    color: white;
                    font-size: 16px;
                    font-style: italic;
                    opacity: 0.9;
                }

                .message {
                    padding: 15px 20px;
                    border-radius: 10px;
                    margin: 20px 0;
                    font-weight: 500;
                    text-align: center;
                }

                .message.success {
                    background: rgba(76, 175, 80, 0.2);
                    color: #4CAF50;
                    border: 1px solid rgba(76, 175, 80, 0.3);
                }

                .message.error {
                    background: rgba(244, 67, 54, 0.2);
                    color: #f44336;
                    border: 1px solid rgba(244, 67, 54, 0.3);
                }

                .loading-overlay {
                    position: absolute;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    background: rgba(0, 0, 0, 0.1);
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    border-radius: 15px;
                }

                @media (max-width: 768px) {
                    .planning-type-apprenants {
                        padding: 10px;
                    }
                    
                    .container-planning {
                        padding: 20px;
                    }
                    
                    .grille-planning {
                        font-size: 12px;
                    }
                    
                    .select-lieu {
                        font-size: 11px;
                        padding: 6px;
                    }
                    
                    .header-jour, .label-creneau {
                        padding: 8px;
                        font-size: 12px;
                    }
                }
            `}</style>

            <div className="planning-type-apprenants">
                {/* Navigation */}
                <div className="navigation">
                    <button
                        onClick={() => router.push('/')}
                        style={{
                            padding: '8px 16px',
                            backgroundColor: '#6b7280',
                            color: 'white',
                            border: 'none',
                            borderRadius: '6px',
                            cursor: 'pointer',
                            fontSize: '14px'
                        }}
                    >
                        ‚Üê Retour Dashboard
                    </button>
                    <div style={{ fontSize: '14px', color: '#6b7280' }}>
                        Admin: {user?.email}
                    </div>
                </div>

                <div className="container-planning">
                    <h1 className="header-title">
                        üè´ Planning Type Apprenants
                    </h1>

                    {/* S√©lecteur d'apprenant */}
                    <div className="selecteur-section">
                        <label className="selecteur-label">
                            üë• S√©lectionner un apprenant :
                        </label>
                        <select 
                            value={apprenantSelectionne?.id || ''}
                            onChange={(e) => handleApprenantChange(e.target.value)}
                            className="select-apprenant"
                        >
                            <option value="">-- Choisir un apprenant --</option>
                            {apprenants.map(apprenant => (
                                <option key={apprenant.id} value={apprenant.id}>
                                    {apprenant.prenom} {apprenant.nom}
                                </option>
                            ))}
                        </select>
                    </div>

                    {/* Message d'information */}
                    {message && (
                        <div className={`message ${message.type}`}>
                            {message.text}
                        </div>
                    )}

                    {/* Grille de planning */}
                    <div className="grille-container" style={{ position: 'relative' }}>
                        {loading && (
                            <div className="loading-overlay">
                                <div style={{ color: 'white', fontSize: '18px' }}>
                                    ‚è≥ Chargement...
                                </div>
                            </div>
                        )}
                        
                        <table className="grille-planning">
                            <thead>
                                <tr>
                                    <th className="header-creneau"></th>
                                    {jours.map(jour => (
                                        <th key={jour} className="header-jour">
                                            {jour.charAt(0).toUpperCase() + jour.slice(1)}
                                        </th>
                                    ))}
                                </tr>
                            </thead>
                            <tbody>
                                {creneaux.map(creneau => (
                                    <tr key={creneau}>
                                        <td className="label-creneau">
                                            {creneau === 'matin' ? 'üåÖ Matin' : 'üåÜ AM'}
                                        </td>
                                        {jours.map(jour => (
                                            <CasePlanning
                                                key={`${jour}-${creneau}`}
                                                jour={jour}
                                                creneau={creneau}
                                            />
                                        ))}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>

                    {/* Actions et statistiques */}
                    <div className="actions-section">
                        <button 
                            onClick={sauvegarderPlanning}
                            disabled={!apprenantSelectionne || loading}
                            className="btn-sauvegarder"
                        >
                            {loading ? '‚è≥ Sauvegarde...' : 'üíæ Sauvegarder Planning'}
                        </button>
                        
                        {apprenantSelectionne && (
                            <div className="stats-planning">
                                ‚ÑπÔ∏è {nbCreneaux} cr√©neau(s) d√©fini(s) pour {apprenantSelectionne.prenom} {apprenantSelectionne.nom}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </>
    )
}

// Protection avec HOC admin
export default withAuthAdmin(PlanningTypeApprenants, "Planning Type Apprenants")

