                    />
                )
                
            case 'absences':
                return (
                    <Absence
                        formateurId={formateurSelectionne.id}
                        formateurData={formateurSelectionne}
                        onSuccess={handleSuccess}
                        onError={handleError}
                    />
                )
                
            case 'planning-hebdo':
                return (
                    <MonPlanningHebdo
                        formateurId={formateurSelectionne.id}
                        formateurData={formateurSelectionne}
                        onError={handleError}
                    />
                )
                
            default:
                return null
        }
    }

