import { useEffect, useState } from 'react'

function TestAdmin1() {
  const [isConnected, setIsConnected] = useState(false)
  const [logs, setLogs] = useState([])
  const [userId] = useState('admin1-test-123')
  const [adminEmail] = useState('admin1@test.aclef')
  const [sessionId, setSessionId] = useState('')

  useEffect(() => {
    setSessionId(`session-${Date.now()}`)
  }, [])
  const [loginError, setLoginError] = useState('')

  const ajouterLog = (message) => {
    const timestamp = new Date().toLocaleTimeString()
    const nouveauLog = `${timestamp}: ${message}`
    setLogs(prev => [...prev, nouveauLog])
    console.log('üß™ ADMIN1:', nouveauLog)
  }

  // üß™ SOLUTION SENDBEACON + XHR POUR FERMETURE
  const deconnexionUrgence = () => {
    try {
      const data = JSON.stringify({
        action: 'logout_emergency',
        userId,
        adminEmail,
        sessionId,
        timestamp: Date.now()
      })

      // Essayer SendBeacon d'abord
      if (navigator.sendBeacon) {
        const success = navigator.sendBeacon('/api/test-verrou-admin', data)
        console.log('üö® SendBeacon d√©connexion urgence:', success ? 'ENVOY√â' : '√âCHEC')
        
        if (success) {
          ajouterLog('üö® D√©connexion urgence SendBeacon envoy√©e')
          return true
        }
      }

      // Fallback XHR synchrone
      const client = new XMLHttpRequest()
      client.open("POST", "/api/test-verrou-admin", false)
      client.setRequestHeader("Content-Type", "application/json")
      client.send(data)

      if (client.status === 200) {
        ajouterLog('üö® D√©connexion urgence XHR envoy√©e')
        console.log('üö® XHR d√©connexion urgence: SUCC√àS')
        return true
      }

    } catch (error) {
      console.error('‚ùå Erreur d√©connexion urgence:', error)
      ajouterLog(`‚ùå Erreur d√©connexion urgence: ${error.message}`)
    }
    
    return false
  }

  // Gestionnaire fermeture d'onglet
  const handlePageUnload = (e) => {
    if (isConnected) {
      console.log('üö™ ADMIN1 - Fermeture d√©tect√©e, d√©connexion urgence...')
      deconnexionUrgence()
    }
  }

  // Setup des event listeners
  useEffect(() => {
    if (isConnected) {
      window.addEventListener('beforeunload', handlePageUnload)
      window.addEventListener('unload', handlePageUnload)
      
      return () => {
        window.removeEventListener('beforeunload', handlePageUnload)
        window.removeEventListener('unload', handlePageUnload)
      }
    }
  }, [isConnected])

  const login = async () => {
    try {
      setLoginError('')
      ajouterLog('üîê Tentative de connexion...')
      
      const response = await fetch('/api/test-verrou-admin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'login',
          userId,
          adminEmail,
          sessionId
        })
      })

      const data = await response.json()

      if (response.status === 200) {
        setIsConnected(true)
        ajouterLog('‚úÖ Connexion r√©ussie - Verrou activ√©')
      } else if (response.status === 423) {
        setLoginError(`‚ùå Acc√®s refus√©: ${data.activeAdmin} est d√©j√† connect√©`)
        ajouterLog(`‚ùå Connexion refus√©e: ${data.activeAdmin} d√©j√† connect√©`)
      } else {
        setLoginError(`‚ùå Erreur: ${data.message}`)
        ajouterLog(`‚ùå Erreur connexion: ${data.message}`)
      }

    } catch (error) {
      setLoginError(`‚ùå Erreur: ${error.message}`)
      ajouterLog(`‚ùå Erreur: ${error.message}`)
    }
  }

  const logout = async () => {
    try {
      ajouterLog('üö™ D√©connexion normale...')
      
      await fetch('/api/test-verrou-admin', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'logout',
          userId,
          adminEmail
        })
      })

      setIsConnected(false)
      ajouterLog('‚úÖ D√©connexion normale r√©ussie - Verrou lib√©r√©')

    } catch (error) {
      ajouterLog(`‚ùå Erreur d√©connexion: ${error.message}`)
    }
  }

  const simulerFermeture = () => {
    ajouterLog('üß™ Simulation fermeture onglet...')
    deconnexionUrgence()
    setIsConnected(false)
  }

  return (
    <div style={{ padding: '20px', fontFamily: 'monospace', backgroundColor: '#e3f2fd', minHeight: '100vh' }}>
      <div style={{ maxWidth: '600px', margin: '0 auto', backgroundColor: 'white', padding: '30px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
        
        <h1 style={{ fontSize: '24px', marginBottom: '20px', color: '#1976d2' }}>
          üß™ Test Admin 1 - Syst√®me Verrou
        </h1>

        <div style={{ marginBottom: '20px', padding: '15px', backgroundColor: isConnected ? '#e8f5e8' : '#fff3e0', borderRadius: '8px', border: '2px solid', borderColor: isConnected ? '#4caf50' : '#ff9800' }}>
          <strong>Status:</strong> 
          <span style={{ marginLeft: '10px', fontWeight: 'bold', color: isConnected ? '#4caf50' : '#ff9800' }}>
            {isConnected ? 'üü¢ CONNECT√â (Verrou actif)' : 'üü° D√âCONNECT√â'}
          </span>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <strong>Admin:</strong> {adminEmail}<br/>
          <strong>User ID:</strong> {userId}<br/>
          <strong>Session:</strong> {sessionId}
        </div>

        {loginError && (
          <div style={{ backgroundColor: '#ffebee', color: '#c62828', padding: '10px', borderRadius: '5px', marginBottom: '20px' }}>
            {loginError}
          </div>
        )}

        <div style={{ marginBottom: '20px' }}>
          {!isConnected ? (
            <button onClick={login} style={{ padding: '12px 24px', backgroundColor: '#1976d2', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', marginRight: '10px' }}>
              üîê Se Connecter
            </button>
          ) : (
            <>
              <button onClick={logout} style={{ padding: '12px 24px', backgroundColor: '#4caf50', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', marginRight: '10px' }}>
                üö™ D√©connexion Normale
              </button>
              
              <button onClick={simulerFermeture} style={{ padding: '12px 24px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', marginRight: '10px' }}>
                üö® Simuler Fermeture Onglet
              </button>
            </>
          )}
          
          <button onClick={() => setLogs([])} style={{ padding: '12px 24px', backgroundColor: '#6b7280', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer' }}>
            üóëÔ∏è Vider Logs
          </button>
        </div>

        <div style={{ backgroundColor: '#1f2937', color: '#f9fafb', padding: '20px', borderRadius: '8px', height: '200px', overflow: 'auto', fontSize: '14px' }}>
          <div style={{ fontWeight: 'bold', marginBottom: '10px' }}>üìä Logs Admin 1:</div>
          {logs.map((log, index) => (
            <div key={index} style={{ marginBottom: '5px' }}>{log}</div>
          ))}
          {logs.length === 0 && (
            <div style={{ color: '#9ca3af', fontStyle: 'italic' }}>Aucun log...</div>
          )}
        </div>

        <div style={{ marginTop: '20px', fontSize: '14px', color: '#6b7280' }}>
          <strong>Test:</strong><br/>
          1. Connecte-toi en tant qu'Admin 1<br/>
          2. Ouvre Admin 2 dans un nouvel onglet<br/>
          3. Essaie de connecter Admin 2 (doit √™tre bloqu√©)<br/>
          4. Ferme cet onglet ou simule fermeture<br/>
          5. R√©essaie Admin 2 (doit pouvoir se connecter)
        </div>
      </div>
    </div>
  )
}

