import { useEffect, useState } from 'react'

function TestDeconnexion() {
  const [logs, setLogs] = useState([])
  const [sessionActive, setSessionActive] = useState(true)
  const [testUserId] = useState('test-admin-123')

  const ajouterLog = (message) => {
    const timestamp = new Date().toLocaleTimeString()
    const nouveauLog = `${timestamp}: ${message}`
    setLogs(prev => [...prev, nouveauLog])
    console.log('üß™ TEST:', nouveauLog)
  }

  // üß™ SOLUTION 1 : SEND BEACON
  const testSendBeacon = () => {
    try {
      const data = JSON.stringify({
        userId: testUserId,
        action: 'deconnexion',
        method: 'sendBeacon',
        timestamp: Date.now()
      })

      if (navigator.sendBeacon) {
        const success = navigator.sendBeacon('/api/test-deconnexion', data)
        ajouterLog(`SendBeacon: ${success ? 'SUCC√àS' : '√âCHEC'}`)
        return success
      } else {
        ajouterLog('SendBeacon NON SUPPORT√â')
        return false
      }
    } catch (error) {
      ajouterLog(`Erreur SendBeacon: ${error.message}`)
      return false
    }
  }

  // üß™ SOLUTION 2 : XHR SYNCHRONE
  const testXHRSynchrone = () => {
    try {
      const data = JSON.stringify({
        userId: testUserId,
        action: 'deconnexion',
        method: 'xhr_synchrone',
        timestamp: Date.now()
      })

      const client = new XMLHttpRequest()
      client.open("POST", "/api/test-deconnexion", false) // false = synchrone
      client.setRequestHeader("Content-Type", "application/json")
      client.send(data)

      if (client.status === 200) {
        ajouterLog(`XHR Synchrone: SUCC√àS (${client.status})`)
        return true
      } else {
        ajouterLog(`XHR Synchrone: √âCHEC (${client.status})`)
        return false
      }
    } catch (error) {
      ajouterLog(`Erreur XHR: ${error.message}`)
      return false
    }
  }

  // üß™ D√âTECTION FERMETURE VS REFRESH
  const detecterTypeEvenement = (e) => {
    const isRefresh = e.persisted || 
                     (window.performance && window.performance.navigation.type === 1)
    return isRefresh ? 'REFRESH' : 'FERMETURE_REELLE'
  }

  // üß™ GESTIONNAIRE PRINCIPAL
  const handlePageUnload = (e) => {
    const typeEvenement = detecterTypeEvenement(e)
    
    if (typeEvenement === 'REFRESH') {
      console.log('üîÑ REFRESH D√âTECT√â - Session pr√©serv√©e')
      return
    }

    console.log('üö™ FERMETURE R√âELLE - Test d√©connexion...')

    // Tester Solution 1
    const beaconSuccess = testSendBeacon()
    
    // Fallback Solution 2
    if (!beaconSuccess) {
      console.log('üîÑ Fallback XHR...')
      testXHRSynchrone()
    }

    setSessionActive(false)
  }

  // üß™ SETUP
  useEffect(() => {
    ajouterLog('üöÄ Test d√©marr√©')

    window.addEventListener('beforeunload', handlePageUnload)
    window.addEventListener('unload', handlePageUnload)

    return () => {
      window.removeEventListener('beforeunload', handlePageUnload)
      window.removeEventListener('unload', handlePageUnload)
    }
  }, [])

  return (
    <div style={{ 
      padding: '20px', 
      fontFamily: 'monospace',
      backgroundColor: '#f8fafc',
      minHeight: '100vh'
    }}>
      <div style={{
        maxWidth: '800px',
        margin: '0 auto',
        backgroundColor: 'white',
        padding: '30px',
        borderRadius: '12px',
        boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
      }}>
        
        <h1 style={{ fontSize: '24px', marginBottom: '20px' }}>
          üß™ Test D√©connexion sur Fermeture
        </h1>

        <div style={{ marginBottom: '20px' }}>
          <strong>Status:</strong> 
          <span style={{
            color: sessionActive ? 'green' : 'red',
            marginLeft: '10px'
          }}>
            {sessionActive ? 'üü¢ ACTIVE' : 'üî¥ D√âCONNECT√âE'}
          </span>
        </div>

        <div style={{ marginBottom: '20px' }}>
          <button
            onClick={() => testSendBeacon()}
            style={{
              padding: '10px 20px',
              marginRight: '10px',
              backgroundColor: '#3b82f6',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            üß™ Test SendBeacon
          </button>

          <button
            onClick={() => testXHRSynchrone()}
            style={{
              padding: '10px 20px',
              marginRight: '10px',
              backgroundColor: '#10b981',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            üß™ Test XHR Synchrone
          </button>

          <button
            onClick={() => setLogs([])}
            style={{
              padding: '10px 20px',
              backgroundColor: '#6b7280',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            üóëÔ∏è Vider Logs
          </button>
        </div>

        <div style={{
          backgroundColor: '#1f2937',
          color: '#f9fafb',
          padding: '20px',
          borderRadius: '8px',
          height: '300px',
          overflow: 'auto',
          fontSize: '14px'
        }}>
          <div style={{ fontWeight: 'bold', marginBottom: '10px' }}>
            üìä Logs de Test:
          </div>
          {logs.map((log, index) => (
            <div key={index} style={{ marginBottom: '5px' }}>
              {log}
            </div>
          ))}
          {logs.length === 0 && (
            <div style={{ color: '#9ca3af', fontStyle: 'italic' }}>
              Aucun log pour le moment...
            </div>
          )}
        </div>

        <div style={{ marginTop: '20px', fontSize: '14px', color: '#6b7280' }}>
          <strong>Instructions:</strong><br/>
          1. Ouvre les outils d√©veloppeur (F12) pour voir les logs console<br/>
          2. Teste les boutons manuellement<br/>
          3. Ferme l'onglet ou actualise pour tester la d√©tection automatique<br/>
          4. V√©rifie les logs dans l'API endpoint
        </div>
      </div>
    </div>
  )
}

