import { useState, useEffect } from 'react'
import { supabase } from '../lib/supabaseClient'

export default function TestSurveillance() {
  const [sessions, setSessions] = useState([])
  const [autoCleanupLogs, setAutoCleanupLogs] = useState([])
  const [isLoading, setIsLoading] = useState(true)
  const [lastUpdate, setLastUpdate] = useState(null)

  // üîç FONCTION DE SURVEILLANCE DES SESSIONS
  const surveillerSessions = async () => {
    try {
      console.log('üîç Surveillance sessions - ' + new Date().toLocaleTimeString())
      
      // R√©cup√©rer toutes les sessions admin
      const { data: sessions, error } = await supabase
        .from('admin_sessions')
        .select('*')
        .order('heartbeat', { ascending: false })

      if (error) {
        console.error('‚ùå Erreur surveillance:', error)
        return
      }

      // Calculer l'inactivit√© pour chaque session
      const sessionsAvecInactivite = sessions.map(session => {
        const dernierHeartbeat = new Date(session.heartbeat)
        const maintenant = new Date()
        const minutesInactif = (maintenant - dernierHeartbeat) / (1000 * 60)
        
        return {
          ...session,
          minutesInactif: minutesInactif.toFixed(1),
          statut: session.is_active ? 
            (minutesInactif > 2 ? 'üíÄ √Ä EXPULSER' : 
             minutesInactif > 1 ? '‚ö†Ô∏è RISQUE' : '‚úÖ ACTIF') : 
            '‚ùå INACTIF'
        }
      })

      setSessions(sessionsAvecInactivite)
      setLastUpdate(new Date().toLocaleTimeString())
      setIsLoading(false)

    } catch (error) {
      console.error('‚ùå Erreur surveillance:', error)
    }
  }

  // ü§ñ FONCTION DE TEST AUTO-CLEANUP
  const testerAutoCleanup = async () => {
    try {
      console.log('ü§ñ Test auto-cleanup manuel...')
      
      const response = await fetch('/api/auto-cleanup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      })
      
      if (response.ok) {
        const result = await response.json()
        const logEntry = {
          timestamp: new Date().toLocaleTimeString(),
          cleaned: result.cleaned,
          success: true
        }
        
        setAutoCleanupLogs(prev => [logEntry, ...prev.slice(0, 9)]) // Garder 10 logs max
        
        if (result.cleaned > 0) {
          console.log(`üßπ AUTO-CLEANUP: ${result.cleaned} session(s) nettoy√©e(s)`)
        } else {
          console.log('‚úÖ Auto-cleanup: Aucune session √† nettoyer')
        }
        
        // Actualiser la surveillance apr√®s cleanup
        await surveillerSessions()
      } else {
        console.warn('‚ö†Ô∏è Auto-cleanup: Erreur serveur')
        setAutoCleanupLogs(prev => [{
          timestamp: new Date().toLocaleTimeString(),
          cleaned: 0,
          success: false,
          error: 'Erreur serveur'
        }, ...prev.slice(0, 9)])
      }
    } catch (error) {
      console.error('‚ùå Erreur test auto-cleanup:', error)
      setAutoCleanupLogs(prev => [{
        timestamp: new Date().toLocaleTimeString(),
        cleaned: 0,
        success: false,
        error: error.message
      }, ...prev.slice(0, 9)])
    }
  }

  // üîÑ SURVEILLANCE AUTOMATIQUE TOUTES LES 30 SECONDES
  useEffect(() => {
    // Surveillance initiale
    surveillerSessions()

    // Interval de surveillance (30 secondes)
    const surveillanceInterval = setInterval(surveillerSessions, 30 * 1000)

    // Interval auto-cleanup (30 secondes)
    const cleanupInterval = setInterval(testerAutoCleanup, 30 * 1000)

    return () => {
      clearInterval(surveillanceInterval)
      clearInterval(cleanupInterval)
    }
  }, [])

  if (isLoading) {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#f3f4f6'
      }}>
        <div style={{ textAlign: 'center', color: '#666' }}>
          <div style={{ fontSize: '24px', marginBottom: '10px' }}>üîç</div>
          <div>Initialisation surveillance...</div>
        </div>
      </div>
    )
  }

  return (
    <div style={{
      minHeight: '100vh',
      backgroundColor: '#f3f4f6',
      padding: '20px'
    }}>
      {/* Header */}
      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        padding: '20px',
        marginBottom: '20px',
        boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
      }}>
        <h1 style={{
          fontSize: '24px',
          fontWeight: 'bold',
          color: '#333',
          margin: '0 0 10px 0'
        }}>
          üîç SURVEILLANCE SESSIONS - TEST AUTO-CLEANUP 2 MINUTES
        </h1>
        <div style={{
          display: 'flex',
