async function createVeronique() {
    try {
        console.log('üß™ [TEST] Cr√©ation de V√©ronique B√©nard pour les tests...')
        
        // V√©rifier si elle existe d√©j√†
        const { data: existing, error: checkError } = await supabase
            .from('users')
            .select('*')
            .eq('prenom', 'V√©ronique')
            .eq('nom', 'B√©nard')
            .single()
            
        if (existing) {
            console.log('‚úÖ [TEST] V√©ronique B√©nard existe d√©j√†:', existing)
            return existing
        }
        
        // Cr√©er V√©ronique B√©nard
        const { data: newUser, error: insertError } = await supabase
            .from('users')
            .insert({
                prenom: 'V√©ronique',
                nom: 'B√©nard', 
                email: 'veronique.benard@formateur.aclef',
                role: 'formateur',
                archive: false,
                password_hash: null, // Pas de mot de passe personnalis√© initialement
                custom_password: null,
                must_change_password: false,
                created_at: new Date().toISOString()
            })
            .select()
            .single()
            
        if (insertError) {
            console.error('‚ùå [TEST] Erreur cr√©ation:', insertError)
            throw insertError
        }
        
        console.log('‚úÖ [TEST] V√©ronique B√©nard cr√©√©e avec succ√®s:', newUser)
        return newUser
        
    } catch (error) {
        console.error('üí• [TEST] Erreur globale:', error)
    }
}

createVeronique().then(() => {
